function (doc0,Types,prevErrors,pick0,depsOnly) {
	var that=this, 
			i, j, e, item, 
			doc = doc0 || that.data.doc,
			buildAll = !!pick0,
			pick = Object.isString(pick0)?pick0:"",
			t = Types || that.Types, 
			names = {}, 
			dirty = {},
			aname="", 
			a={}, sn={}, pp={}, src = {}, fin = {}, 
			err = prevErrors || {},
