	if (doc.site && doc.site.previewBase) z = doc;

	if (!z._attachments) z._attachments = {};

	// gen work structs
	for (i=0;i<doc.items.length;i++) {
		e = doc.items[i];
		if (e) {
			aname = that.Path(e);
			names[aname]=true;
			sn[aname]=e.snippet;
			src[aname]=doc.items[i];
			if (Object.isFunction(t[e.type].postprocess)) pp[aname] = t[e.type].postprocess;
		}
	}


	// stringify
	for (i in names) {
		if (names.hasOwnProperty(i)) {
			_convert (i);
		}
	}


	// Resolves include directives.
	// Possible syntaxes are:
	// {{res.name}} 			– inserts as is
	// {/*{{res.name}}*/} – inserts as is
	// [/*{{res.name}}*/] – inserts as is
	// "{{res.name}}" 		– inserts stringified version

	// parse includes
	for (i in a) {
		if (a.hasOwnProperty(i)) {
			_parseIncludes (i);
		}
	}

	// merge snippets deps into files 
	for (i in a) {
		if (a.hasOwnProperty(i) && !sn[i]) {
			e = src[i]; 
			var tmp = {};
			for (j in e.state.depends) 
				if (sn[j] && src[j].state.depends) 
					$.extend(tmp, src[j].state.depends);
			$.extend(e.state.depends, tmp);
		}
	}

	// merge files deps into files 
	var tmp = {};
	for (i in a) {
		if (a.hasOwnProperty(i) && !sn[i]) {
			e = src[i]; 
			if (e) {
				tmp = {};
				for (j in e.state.depends) {
					if (e.state.depends.hasOwnProperty(j) && a[j] && !sn[j] && src[j].state.depends) {
						$.extend(tmp, src[j].state.depends);
					}
				}
				$.extend(e.state.depends, tmp);
			}
		}
	}

	// build impacts (reverse deps)
	for (i in a) {
		if (a.hasOwnProperty(i)) {
			e = src[i];
			if (e) {
				for (j in e.state.depends) {
					if (e.state.depends.hasOwnProperty(j)) {
						if (src[j]) src[j].state.impacts[i] = true;
						else {
							//console.log("Missing include "+j+" in "+i);
							_e(i, "Missing include '"+j+"'")
						}
					}
				}
			}
		}
	}

	if (depsOnly) return {
		err: err,
		doc:doc
	};

	// build list of items to be updated
	var imps = {};
	for (i in a){
		if (a.hasOwnProperty(i) && (!pick || i === pick)) {
			e = src[i];
			if (!!buildAll || e.state.sdbm !== e.state.savedSdbm) {
				$.extend (true, imps, e.state.impacts);
				if (!e.snippet) $.extend (true, dirty, e.state.depends);
				dirty [i] = true;
			}
		}
	}
	// add dependees to dirty list, 
	// they also need to be updated
	for (i in imps) {
		if (imps.hasOwnProperty(i)) {
			e = src[i];
			if (e) {
				if (!e.snippet) $.extend (true, dirty, e.state.depends);
				dirty [i] = true;
			}
		}
	}

	console.log("Dirties wired to rebuild: ", dirty);

	// build snippets
	for (i in dirty) {
		if (dirty.hasOwnProperty(i) && sn[i]) {
			try {
				_resolveIncludes (i);
			} catch (er) {
				_e(i, "Error resolving snippet include: "+er.message);
			}
