			}
		}
	}

	// validate finalized versions
	for (i in dirty) {
		if (dirty.hasOwnProperty(i) && !sn[i] && !err[i] && src[i]) {
			fvalidator = null;
			isinvalid = null;
			try {
				fvalidator = (t[src[i].type].manifest || t[src[i].type].extend || {}).validator;
				if (Object.isFunction(fvalidator)) {
