		"#btn-apply": {
			"bind": function (d,v,$o) {
				var f, that = this,
					$r = $o.my().root,
					$c = $r.find("#x1"),
					$img = $r.find("#source"),
					$c2 = $r.find("#x2"),
					n={k:d.scale}, img="",
					width=d.width*1.5;

				if (v!=null && d.cropped) {
					_create (d.crop);

					d.data = $o.my("find","#x1")[0]
						.toDataURL('image/jpeg',0.97)
						.substr(23);
					$o.my().root.trigger("commit");
				}

				function _create (c){
					if(parseInt(c.w) > 0) {
						var img = $img[0], cs, ctx, cs2, ctx2, 
							k = n.k,
							w = c.w*k, // scale width/height
							h = c.h*k; 

						if (!that.Active) {
							cs = $c[0];
							cs.width=d.width;
							cs.height=d.height;
							ctx = cs.getContext("2d");
							ctx.drawImage(img, (c.x*k).floor(), (c.y*k).floor(), d.width*1, d.height*1, 0, 0, d.width*1, d.height*1);

						} else { 
							if (w>d.width*2) {
								cs2 = $c2[0];
								cs2.width=d.width*2;
								cs2.height=2*d.height;
								ctx2 = cs2.getContext("2d");
								ctx2.drawImage(img, c.x*k, c.y*k, w, h, 0, 0, d.width*2,d.height*2);

								cs = $c[0];
								cs.width=d.width;
								cs.height=d.height;
								ctx = cs.getContext("2d");
								ctx.drawImage(cs2, 0, 0, d.width*2,d.height*2, 0, 0, d.width*1, d.height*1);
							} else {
								cs = $c[0];
								cs.width=d.width;
								cs.height=d.height;
								ctx = cs.getContext("2d");
								ctx.drawImage(img, c.x*k, c.y*k, w, h, 0, 0, d.width*1, d.height*1);
							}
						}
					}
				}
			},
			"events": "click.my",
			watch:"#folder",
			css:{
				":disabled": function(d){ return !Object.size(d.crop)}
			}
		},
		"#btn-close": {
			"bind": function (d,v,$o) {if (v!=null) $o.trigger("cancel");},
			"events": "click.my"
		}
	}
