({
	init: function($o, form){ 
		$o.formgen(this.HTML);
		var that = this, d = form.data;
		if (!(d.creds.crypto+"")) {
			$.extend(!0, that.s3, d.s3);
		} else if (cw.crypto.has(d.creds.crypto)) {
			if (!d.creds.CRYPTO) {
				d.creds.crypto = "";
			}
			else {
				var s3 = cw.crypto.dec(d.creds);
				if (!s3.CRYPTO) {
					$.extend(!0, that.s3, s3.s3);
				}
				else {
					that.nokey = true;
				}
			}

		} else {
			that.nokey = true;
		}
	},
	nokey:false,
	s3:{
		accessKeyId:'',
		secretAccessKey:'',
		region:''
	},
	ui:{
		"#s3title":{
			bind:"name",
			check:/^.+$/
		},
		"#s3id":{
			bind:"this.s3.accessKeyId",
			check:/^.+$/
		},
		"#s3key":{
			bind:"this.s3.secretAccessKey",
			check:/^.+$/
		},
		"#s3region":{
			bind:"this.s3.region",
			check:/^[a-z0-9]{2}\-[a-z0-9]{2,20}\-[0-9]$/i
		},
