		},
		"#url":{
			bind:"site.siteBase",
			check:/^http[s]?:\/\/.{3,200}$/
		},
		"#preview":{
			bind:"site.previewBase",
			check:/^http[s]?:\/\/.{3,200}$/
		},
		"#btn-ok":{
			bind: function(d,v,$o) {
				if (v!=null) {
					var that = this, i;
					if (d.creds.crypto+"" && !that.nokey) {
						for (i in d.s3) d.s3[i]="";
						d.creds.s3 = Object.clone(Object.reject(that.s3,"CRYPTO"), true);
						d.creds = cw.crypto.enc(Object.reject(d.creds,"CRYPTO"));
					} else if (!(d.creds.crypto+"")) {
						$.extend(d.s3,Object.reject(that.s3,"CRYPTO"));
					}

					$o.trigger("commit");
				}
			}
		},
