({

	// Editor

	"#warn":{
		bind: function(d, v, $o){
			var r, that = this;
			if (Object.isFunction(that.validator)){
				try {
					r = that.validator(d);
				} catch (e) {
					r = e.message;
				}
			}
			$o.html(
				r
				?'&nbsp;<span class="'+(that.Err?'pseudolink red':'')+'">'+r+'</span>'
				:""
			);
			$o.toggleClass("hide", !r);
			$o.my("find","#xeditor").toggleClass("b1r", !!r);
		},
		watch:"#editor",
		delay:500
	},

	"#fname":{
		bind: function(d){
			var html="", 
					base = this.GetSite().site.previewBase, 
					p = !d.snippet && !!base;
			if (p) html+='<span class="gray fs85 fi-eye"></span>&nbsp;';

			html+= (d.folder?'<span class="gray">'+d.folder+"/</span>":"");

			if (p) {

				html+='<a target="_blank" href="'
						+(base?base.replace(/\/$/,'')+"/":"")
						+(d.folder?d.folder+"/":"")
						+d.name+"."+d.ext
						+'">';
			}
			html+= d.name+"."+d.ext;

			if (p) html+='</a>';

			return html;
		}
	},
	"#xparse":{
		css:{hide: function(d){return !d.parserActive}},
		watch:"#fname"
	},
	"#btn-settings":{
		bind: function(d,v,$o) {
			var that = this, $f;
			if (v!=null) {
				$f = $o;//.my().root;
				if ($f.data("modal")) $f.modal(true);
				else $f.modal({
					manifest:$.extend({}, this.Settings, { RT:this.RT }),
					data:Object.select(d, 
						"id type desc folder mime size name ext cache parserActive noIncludes snippet external".split(" ").add([/^[A-Z]/])
					),
					done: function(err){
						if (err) {
							(window.l||cw.lib).note("Fix errors","error");
							return true;
						}
					},
					esc:true,
					enter:true,
					align:"top:0;right:0", 
					// nose:"top",
					root:$o.my().root,
					screen:"rgba(255,255,255,0.5)",
				}).then(function(res){

					// moveAtt
					var nold = that.RT().Path(d),
							nnew = that.RT().Path(res),
							doc = that.RT().data.doc
					if (nold != nnew && doc._attachments) {
						//doc._attachments[nnew] = doc._attachments[nold];
						delete doc._attachments[nold];
					}
					_fin();

					function _fin(){
						$.extend(d, res);
						$o.my("find","#fname").trigger("check");
					}
				});
			}
		},
		watch:"#fname"
	},
	"#showhideproxy":{
		bind: function(d,v,$o) {
			$o.my().root.toggleClass("hide", this.GetActiveId() !== d.id);
		}
	}
})

({
	// Parser

	"#btn-deps":{
		bind:function(d,v,$o){
			if (v!=null) {
				var that = this, h="";

				that.RT().Items2Atts(null, null, {}, true, true);

				var deps=Object.keys(d.state.depends||{}),
						imps = Object.keys(d.state.impacts||{});

				//console.log(d);

				if (deps.length) {
					h+='<div>Depends:</div><div class="fs80 lh130 gray">'
						+deps.map(function(i){
							return d.state.depends[i] ? i:'<span class="red">'+i+'</span>';
						}).join("<br>")
						+'</div>';
				}

				if (imps.length) {
					h+='<div>Impacts:</div><div class="fs80 lh130 gray">'
						+imps.join("<br>")
						+'</div>';
				}

				if (h) {
					$o.modal({
						manifest:$.extend(true, {},that.Confirm,{params:{width:250}}),
						width:250,
						data:{
							text:h, yes:"Ok", 	no:"", cancel:""
						},
						align:"top:103%",
						nose:"top",
						bound:10,
						screen:"rgba(255,255,255,0.5)",
						css:"fs100 lh170 pb20",
						esc:true
					})
				}
				else {
					cw.lib.note("Item has no dependencies.","ok");
				}
			}
		},
		watch:"#fname"
	},

	"#btn-rendertab":{
		bind:function(){},
		/*css:{
			":disabled": function(d){return !!d.snippet}
		},*/
		watch:"#fname"
	},

	"#btn-play":{
		delay:100,
		bind: function (d,v,$o) {
			var parser, res, err, atts={}, html="", that=this;
			if (v!=null) {

				if(d.parserActive) {
					try {
						parser = eval(d.parser);
					} catch (e){ 
						console.log(err="Parser code has errors, "+e.message, e.stack);
					}

					if (Object.isFunction(parser)) {
						try {
							res = parser(d, atts)+"";
						} catch(e){
							console.log(err="Parser/generator error, "+e.message, e.stack);
						}
					}
				} 

				if (res) {
					$o.modal({
						manifest:that.PlayParser,
						data:{txt:res},
						esc:true,
						enter:true,
						width:800,
						global:true,
						screen:true
					})
				}
				if (err) {
					(window.l||cw.lib)["note"](err,"error");
				}
			}
		},
		check:function(d,v,$o){
			var parser, err;
			try {parser = eval(d.parser);} catch (e){}
			err = (!Object.isFunction(parser)?"Error":"");
			$o.toggleClass("red",!!err);
			return err;
		},
		watch:"#parser"
	},
	"#parser":{
		bind:"parser",
		init: function($o, form){
			CodeMirror.fromTextArea($o[0], {
				mode: "javascript",
				lineNumbers:true,
				indentWithTabs:!0,
				indentUnit:2,
				tabSize:2,
				theme: "default"
			});
		}
	},
	"#btn-source":{
		bind: function(d,v,$o){
			if (v!=null) {
				var $xp = $o.my("find","#xparser"),
						$e = $o.my("find","#editor");

				$e.parent().toggleClass("hide");
				$xp.toggleClass("hide");
			}
		}
	},
	"#btn-parser":{
		bind: function(d,v,$o){
			if (v!=null) {
				var $xp = $o.my("find","#xparser"),
						$e = $o.my("find","#editor");

				$e.parent().toggleClass("hide");
				$xp.toggleClass("hide");
			}
		},
		css:{
			"hide": function(d){return !d.parserActive}
		},
		watch:"#fname"
	}
})

