(function (doc0, opts0, $o) {

	var i,j,that = this,
			d = that.data,
			pi = $.Deferred(),
			tdoc = doc0 || that.data.doc,

			opts = $.extend(!0,{ 
				buttons:true, 
				footer:"", 
				checkcreds:false,
				pick:"",
				resolve:true,
				silent:false,
				blocking:true
			},Object.reject(opts0||{},"errors")),

			errors = (opts0||{}).errors || {},
			key,
			items={};

	for (i=0;i<tdoc.items.length;i++) {
		if ( !tdoc.items[i].snippet && (j=that.Path(tdoc.items[i])).to(1) != ".") {
			items[j.from(1)] = tdoc.items[i];
		}
	}


	if (key = d.doc.creds.crypto) {
		if (!cw.crypto.has(key)) {
			// cw.lib.note("No cryptokey to decode CouchDB credentials.", "error");
			if (opts.checkcreds) pi.reject("Can’t get S3 credentials.");
			that.creds.s3 = {region:""}
		} else {
			var s3 = cw.crypto.dec(d.doc.creds)["s3"];
			if (s3) {
				that.creds.s3 = s3;
			} else {
				cw.lib.note("Error decoding S3 credentials.", "error");
				if (opts.checkcreds) pi.reject("Can’t get S3 credentials.");
				that.creds.s3 = {region:""}
			}
		}
	} else {
		that.creds.s3 = Object.clone(d.doc.s3, !0);
	}

	try {
		if (opts.resolve) that.ResolveExternals(tdoc, errors).always(_process);
		else _process();

	} catch(e) {
		pi.reject("Error during validation", e.message, e.stack);
	}

	return pi.promise();


	//- - - - - - - - - - - - - - -

	function _process (){
			var a = that.Items2Atts( tdoc,  that.Types,  errors,  opts.pick?opts.pick:true);

			//a.result._id = that.creds.s3.region;

			if (Object.size(a.err)) {
				$o.modal({
					manifest:that.ParseErrors,
					data:$.extend(!0, {}, opts,a ),
					esc:true,
					enter:true,
					screen:opts.blocking?'rgba(255,255,255,0.5)':false,
					align:"top:105%",
					nose:"top",
					bound:10,
					drag:!opts.blocking
				})
				.then(function(){
					_result(a);
				})
				.fail(function(){
					pi.reject("Cancelled");
				});
			} 
			else {
				if (!opts.silent) cw.lib.note("Doc integrity seems to be Ok.");
				_result(a);
			}
		}


	//- - - - - - - - - - - - - - -

	function _result(a){
		if (a.result) {
			var att  = a.result._attachments;

			// Make it last node of the doc
			delete a.result._attachments;
			a.result._attachments = att;

			//console.log(a);

			// load binary atts
			that.db.get(d.doc._id, {
				attachments:true
			})
			.then(
				function(res){
					if (res._attachments) for (var i in res._attachments) {
						if (!a.result._attachments[i] || null == a.result._attachments[i].data) {
							a.result._attachments[i] = {
								content_type:res._attachments[i].content_type,
								data:res._attachments[i].data
							}
						}
						if(items[i]) items[i].state.crc = $.my.f.sdbmCode(res._attachments[i].data);
					};
					pi.resolve(a);
				},
				function(){
					if (!opts.silent) cw.lib.note("Doc is not yet saved, you may need to save doc.", "warning");
					pi.resolve(a);
				}
			);
		}

	} // --end _result

})

(function (doc0, errs) {
