// Sample ToDo CloudWall application
({
	// Data structure of an app, 
	// reflects controls’ states
	data:{ 
		cmd:"",
		filter:["All"],
		editor:{
			// Doc struct to edit/save
			_id:"", name:"", 
			type:"todo", stamp:"", 
			creator:"", 
			top:{done:"", text:"", stamp:""},
			list:[{done:"", text:"", stamp:"", isnew:true}], log:[]
		},
		key:"_"
	},
	// Init function, renders HTML
	// and start some event monitors
	init: function ($o, form) {
		var that=this, d = form.data;
		$o.html(that.FormHTML);
		//enc key
		form.data.key= form.data.editor.crypto||"_";
	},
	// UI↔data bindings
	ui:{
		"#name":"editor.name",
		"#encrypt":{
			bind:function(d,v,$o){
				if (v!=null) {
					d.key=v;
					if (v!="_") d.editor.crypto=v;
					else delete d.editor.crypto;
				}
				return v||d.key;
			},
			init: function($o) {
				$o.html(
					[ {id:"_",name:"No encryption"} ]
					.add( cw.crypto.keys() )
					.reduce(function(a,b){
						return a+('<option value="{id}">{name}</option>').assign(b);
					},"")
				).select2();
			}
		},
		"#doctags":{
			bind:"editor.tags",
			init: function($o){
				$o.select2({
					tags:this.db&&this.db.tags?this.db.tags():[],
					placeholder:"Doc tags"
				});
			}
		},
		"#list":{
			bind:"editor.list",
			manifest:"ToDoLine",	// Defined later
			list:'<div class="todo"></div>',
			init:function($o, form) {
				$o.sortable();			// make items draggable
			}
		},
		"#filter":{
			bind:function (d,v,$o){
				if (v!=null) d.filter=v;
				$o.my("find","#list")
					.removeClass("todoAll todoDone todoActive")
					.addClass("todo"+d.filter);
				return d.filter;
			},
			init: function ($o) {
				$o.tags({tags:[["Active","Done"]], empty:{"All":"All"}});
			}
		},
		"#btn-save":{
			bind: function (d,v,$o){ if (v!=null) $o.trigger("commit");},
			events:"click",
			css:{
				":disabled":function (d){ return !d.editor.name }
			},
			watch:"#name"
		},
		"#btn-cancel":{
			bind:function(d,v,$o){ if (v!=null) $o.trigger("cancel");},
			events:"click.my"
		},
		"#atts":{
			delay:50,
			init: function ($o, form) {
				$o.my("cw.Sys.Attachments", {
					doc:form.data.editor,
					truncate:20
				});
			},
			bind: function (){}
