manifest.Search:
function (re0) {
		var that = this,
				vi = {},
				re = that.SearchRe || re0;

		if (!re) return that.data.found = {};

		if (that.Index.dirty) {
			that.Index.idx = _index(that.Doc.value);
			that.Index.dirty = false;
		}

		for (var m = 0, row, idx = that.Index.idx, i=0, pl = 0; i<idx.length; i++) {
			m = 0;
			row = idx[i];
			pl = row.p.length;
			if (re.test(row.k)) m = m | 4;
			if (row.v && re.test(row.v)) m = m | 8;
			if (m) for (var tp, j=1; j<pl; j++) {
				tp = row.p.slice(0,j);
				vi[cw.lib.sdbm(tp.join('❀') +'❀'+row.p[j])] = j == pl-1?m:3;
			}
		}

		return that.data.found = vi;

		// - - - - - -

		function _index(doc) {
			var stack = ['Root'], a=[];
			function _ob(v){
				Object.keys(v).forEach(function(i){
					var t = $.type(v[i]);
					if (t == 'object' || t=='array') {
						//a.push({k:i.toLowerCase(), p:stack.slice(0).add(i), v:false});
						stack.push(i);
						_ob(v[i]);
						a.push({k:i.toLowerCase(), p:stack.slice(0), v:false});
						stack.pop();
					}
					else if (t == 'string') {
						a.push({
							k:i.toLowerCase(),
							p:stack.slice(0).add(i),
							v:v[i].toLowerCase().replace(/[\r\n\t\s]+/g,' ')
						});
					}
					else {
						a.push({
							k:i.toLowerCase(),
							p:stack.slice(0).add(i), 
							v:(v[i]+'')
						});
					}
				})
			}
			_ob(doc);
			return a;
		}
	}

