manifest.MakeList:
function (data,key,state) {

	// Object/array processor,
	// generates typed metaobject

	var that=this,
			a=[], 
			b = data.value,
			sdbm = cw.lib.sdbm,
			atob = window.atob,
			vs = that.data.viewstate || {},
			found = that.data.found || {},
			level = data.level + 1,
			isSearch = that.data.search.length > 2;

	Object.keys(b)
	.forEach(function(i){
		var val = b[i],
				dt = $.type(val),
				man = dt == 'object' || dt == 'array' ? 'multi' : dt,
				hash,
				bin;

		// viewstate
		var path = data.path.slice(0).add(data.key),
				vkey = cw.lib.sdbm(path.join('❀')+'❀'+i);

		if (isSearch && !found[vkey]) return;

		// Detect string-derived types
		if (dt == 'string'){
			if (isSearch && (found[vkey] | 8)) {
				man = 'found';
				hash = sdbm(val);
			}
			else if (val.length > 11 && val.to(8) == 'function' && /\}[\r\n\t\s]*$/.test(val.substr(-10))) {
				man = 'function';
			}
			else if(
				val.length>160 && val.length < 150e3
				&& /^[a-zA-Z0-9\+\/]+$/.test(val.substr(0,158))
				&& /^[a-zA-Z0-9\+\/]+={0,2}$/.test(val)
			) {
				bin = _atob(val);
				hash = sdbm(val);
				man = 'base64';
			}
			else if(
				val.length>160 && val.length < 150e3
				&& /^data:image\/(jpeg|png|gif);base64,/.test(val)
			) {
				bin = _atob(val.replace(/^data:image\/(jpeg|png|gif);base64,/,''));
				hash = sdbm(val);
				man = 'base64';
			}
			else hash = sdbm(val);
		}

		if (!isSearch || found[vkey]) a.push({
			state:state?state:!isSearch?vs[vkey] || 1:found[vkey],
			level:level,
			manifest:'$'+man,
			path:  path,
			type:  dt,
			key:   i,
			id:    i,
			value: b[i],
			bin:   bin,
			hash:  hash
		});
	});

	return a;

	// - - -

	function _atob(s){
		var bin = '';
		try {
			bin = atob(s);
		} catch(e){}
		return bin;
	}

}

manifest.Ls:
