			else return null;
		}
	};

	return storage;
}

manifest.j2h:
function j2h(source,maxChars,startKeys,depth,keysOnly) {

	var k0 = startKeys || [],
			k1 = cw.lib.a2o(k0),
			ko = !!keysOnly;
	var h = [];

	_unwrap(_parse(source, maxChars, 0));

	return h;

	// ================================================

	function _parseObj(src, chars, lvl) {

		var res = {
			key:null,
			val:null,
			empty:false,
			size:2, 
			left:'{', 
			right:'}',
			trim:0,
			type:'object',
			rows:[]
		};

		var i, j, tval, ckey, tkey;
		var len = 0;

		var skip = {},
				k = Object.keys(src);

		// empty obj
		if (!k.length) {
			res.empty = true;
			res.size = 2;
			res.val = '';
			return res;
		}

		// return shortcut, no traverse
		if (lvl >= depth || k.length>50) {
			res.trim = k.length;
			res.size = 2;
			return res;
		}

		// 

		var childchars = (1.5 * chars / k.length | 0).clamp(10, chars*0.9 | 0),
				ctr = 0,
				skip = {};

		// Process priviledged keys
		for (i=0; i<k0.length && len < chars - childchars/3; i++) {
			ckey = k0[i];
			if (undefined != src[ckey]) {
				skip[ckey] = true;
				tkey = ckey.truncate((childchars/(ko?1:2)).clamp(12,100),'right','…');
				if (/\s/.test(tkey) || !tkey) tkey = '"'+tkey+'"';
				tval = _parse(src[ckey], childchars, lvl+1);
				tval.key = tkey;
				len += tkey.length + (ko?0:tval.size);
				ctr+=1;
				res.rows.push(tval);
			}
		}

		// Other keys
		for (i=0; i<k.length && len < chars - childchars/3; i++) {
			ckey = k[i];
			if (!skip[ckey]) {
				tkey = ckey.truncate((childchars/(ko?1:2)).clamp(12,100),'right','…');
				if (/\s/.test(tkey) || !tkey) tkey = '"'+tkey+'"';
				tval = _parse(src[ckey], childchars, lvl+1);
				tval.key = tkey;
				len += tkey.length + (ko?0:tval.size);
				ctr+=1;
				res.rows.push(tval);
			}
		}
		res.trim = k.length - ctr;
		res.size = len;
		return res;
	}


	// ================================================

	function _parse(src, chars, lvl) {
		// returns {size: charsSpent, left:'{', right:'r', rows:[]}
		// strings in .r should be treated as a markup

		var res = {
			key:null,
			val:null,
			empty:false,
			size:0, 
			left:'', 
			right:'',
			trim:0,
			type:'',
			rows:[]
		};
		var i, j, tval, tdiff;
		var len = 0,
				clen = 0,
				childchars = 0,
				type = typeof src;

		// we have simple type ----------------------------
		if (null===src ||  type != 'object') {

			if (type != 'string') {
				res.type = type!='number'?'simple':type;
				tval = src+'';
			}
			else {
				res.type = 'string';
				clen = src.length;
				if (clen <= chars-1) tval = src;
				else {
					tval = src.substr(0, chars);
					res.trim = clen - chars;
				}
				if (!tval.length || tval[0]=='-' || /\s/.test(tval)) {
					res.left = res.right = '"';
					len+=2;
				}
			}
			len += tval.length;
			res.size = len;
			res.empty = !tval.length;
			res.val = tval;
			return res;
		} 

		// we have an array -----------------------------
		else if (Object.isArray(src)) {
			res.type='array';
			res.left='['; res.right=']'; len +=2;
			// empty arr
			if (!src.length) {
				res.empty = true;
				len = 2;
				res.val = '';
			}
			// array with elts
			else {
				if (lvl < depth && src.length<50 && chars / src.length > 5) {
					var childchars = (1.5 * chars / src.length | 0).clamp(10, chars*0.9 | 0),
							clen = 0,
							ctr = 0;

					for (i=0; i<src.length && clen < chars - childchars/3; i++) {
						// recursion
						tval = _parse(src[i], childchars, lvl + 1);
						len += tval.size;
						clen += tval.size;
						ctr+=1;
						res.rows.push(tval);
					}

					res.trim = src.length - ctr;

				}
				else res.trim = src.length;
			}
		}

		// we have an object ----------------------------
		else {
			return _parseObj(src, chars, lvl);
		}

		// return result

		res.size = len;
		return res;
	}


	// ================================================


	function p(a,b){ h.push([a,b]);}

	function _unwrap(x){

		if (null != x.key) {
			p('key', x.key);
			if (!ko) p('', ': ');
		}

		// empty
		if (x.empty) {
			p('empty', x.left+(x.val||'')+x.right); 
			return;
		}

		var t = x.type,
				valset = false;

		// val defined
		if (x.val != null){
			if (x.left) p('bracket', x.left);
			if (x.val.length < maxChars/5 && k1[x.key]) {
				valset = true;
			}
			if (!ko || valset) {
				if (ko && valset) p('', ': ');
				p(t, x.val.escapeHTML());
				if (x.trim) {
					var prefix = '+';
					if (t == 'object' || t == 'array') 	p('trim', prefix+x.trim);
					else p('trim','…');
				}
			} 
			if (x.right) p('bracket', x.right);

			if (!valset && ko && !x.right && t !='simple' && t !='number') {
				p('bracket', t=='string'?'""':t=='object'?'{}':'[]')
			}
		}

		// rows

		else if (x.rows.length){
			if (x.left) p('bracket', x.left);

			var i, rl = x.rows.length;
			for (i=0; i<rl;i++) {
				_unwrap(x.rows[i]);
				if (i!=rl-1 || x.trim) p('',', ');
			}

			if (x.trim) {
				var prefix = '+';
				if (t == 'object' || t == 'array') 	p('trim', prefix+x.trim);
				else p('trim','…');
			}
			if (x.right) p('bracket', x.right);
		}

		// no rows, trim
		else if (x.trim) {
			if (t == 'object') 			p('short', '{'+x.trim+'}');
			else if (t == 'array') 	p('short', '['+x.trim+']');
		}
	}

}

Event.add:
function (evt,data,that,$o) {
	evt.stopPropagation();
	var $b = $(this),
			$f = $b.parents(".my-form").eq(0);		
	var $p = $b,
			p = $f.data("my").data,
			mn = $f.data("my").manifest,
			t = $.type(p.value),
			isa = t=="array",
			key = $f.my("data").key,
			clip = that.Ls("Clip")||{type:""},
			sample = {
				object:{},
				array:[],
				string:"",
				boolean:true,
				number:0
			};
	$b.parent().addClass("hover");
	$f.addClass("hover");
	$(this).modal({
		manifest:that.MAdd,
		data:{
			cmd:'prepend',
			text:'New '+(isa?'member':'property')+' in <b>'+p.key+':'+(isa?'[]':'{}')+'</b>',
			prepend:(isa?'Insert':'Prepend'),
			append:(isa?'':'Append'),
			isarray:isa,
			key:isa?"0":'',
			clip:clip,
			keys:isa?{}:Object.keys(p.value).reduce(function(a,b){a[b]=true;return a;},{}),
			file:{}
		},
		width:300,
		align:"top:100%",
		root:$o,
		bound:1,
		nose:"top",
		screen:"rgba(255,255,255,0)",
		close:false,
		esc:true,
		enter:true
	})
	.then(function(data){
		var i, cmd = data.cmd,
				redraw = true, 
				keys=[], vals=[], ins;

		if (data.clip.type && data.type=="clip") sample.clip = data.clip.value; 

		ins = Object.clone(data.type=="file"?data.file:sample[data.type],true);

		if (!data.isarray) {
			if (data.type=="string" && data.file.data) ins="data:"+data.file.content_type+";base64,"+data.file.data;
			else delete ins.name;
		}

		if (p.level==1 && /^(_id|_rev)$/.test(data.key)) {
			cw.lib.note("Can’t change system properties.","error");
			return;
		}

		// Object
		if (/^(ob)/.test(t)) {

			// Append/replace is simple
			if (cmd=="append" || p.value[data.key]!=null) { 
				p.value[data.key] = ins; 
			}

			// Prepend is more complex
			else if (cmd=="prepend") {

				keys = Object.keys(p.value);
				for (i in keys) {
					vals.push(p.value[keys[i]]);
					delete p.value[keys[i]];
				}
				p.value[data.key] = ins;
				for (i in keys) {
					if (keys[i]!==data.key) p.value[keys[i]] = vals[i];
				}
				vals=keys=null;
			}
			//Unknown cmd
			else redraw = false;
		}

		// Array
		else if (isa) {
			p.value.insert(ins, data.key);
		}
		else redraw = false;

		if (redraw) {
			// force redraw
			mn.ShowItems = null;
			mn.List = that.MakeList(p);
			p.state=2;
			$f.find(".idesc:eq(0)").trigger("check");
			$f.find(".ilist:eq(0)").trigger("recalc");
		}
	})
	.always(function(){
		$b.parent().removeClass("hover");
		$f.removeClass("hover");
	});
}

Event.takeout:
function (evt,data,that,$o) {
	evt.stopPropagation();
	var $b = $(this),
			$f = $b.parents('.my-form').eq(0),
			p = $f.data('my').data,
			mn = $f.data('my').manifest;
	$b.parent().addClass('hover');
	$f.addClass('hover');
	$b.modal({
		manifest:that.MTakeout,
		data:{
			json:JSON.stringify(p.value,null,'  '),
			path:p.path,
			key:p.key,
			replace:Object.size(p.value)?'Replace':'Save',
			extend:Object.size(p.value)?'Extend':''
		},
		width:($f.width()-160).clamp(500,1000),
		align:'top:103%;right:-76px;',
		//root:$o,
    root:$o.find('section#editor').eq(0),
		nose:'top',
		screen:'rgba(255,255,255,0)',
		close:true,
		esc:true,
		enter:true
	})
	.then(function(res){
		var redraw = true,
				res1;

		if (that.my.parent() && that.my.parent().data.isNew) res1 = res.node;
		else res1 = p.level == 0 ? Object.reject(res.node,/^(_id|_rev)$/) : res.node;

		if (res.cmd == 'extend') {
			Object.merge( p.value,  res1, true);
		}
		else if (res.cmd == 'replace') {
			var temp = {};
			if (p.level == 0 && p.value._rev) temp = Object.select(p.value, /^(_id|_rev)$/);
			that.RT('Lib').kick(p.value, res1);
			if (p.level == 0) Object.merge(p.value, temp);
		}
		else redraw = false;

		if (redraw) {
			// force redraw
			mn.ShowItems = null;
			p.state=2;

			that.RT('Lib').relist($f, mn);
		}

	})
	.always(function(){
		$b.parent().removeClass('hover');
		$f.removeClass('hover');
	});
}

