						{
							'Esc':_cancel,
							'Ctrl-Enter':_commit.debounce(20),
							'Cmd-Enter':_commit.debounce(20)
						}, 
						!isNumber?{}:{'Enter':_commit},
						d.commitKeys.reduce(function(a,b){ a[b] = _commit; return a;}, {}),
						d.cancelKeys.reduce(function(a,b){ a[b] = _cancel; return a;}, {})
					)
				});

