(function(evt, data, that, $o){
	evt.stopPropagation();
	var $b = $(this),
			$f = $b.parents(".my-form").eq(0);		
	var $p = $b,
			p = $f.data("my").data,
			mn = $f.data("my").manifest,
			t = $.type(p.value),
			isa = t=="array",
			key = $f.my("data").key,
			clip = that.Ls("Clip")||{type:""},
			sample = {
				object:{},
				array:[],
				string:"",
				boolean:true,
				number:0
			};
	$b.parent().addClass("hover");
	$f.addClass("hover");
	$(this).modal({
		manifest:that.MAdd,
		data:{
			cmd:'prepend',
			text:'New '+(isa?'member':'property')+' in <b>'+p.key+':'+(isa?'[]':'{}')+'</b>',
			prepend:(isa?'Insert':'Prepend'),
			append:(isa?'':'Append'),
			isarray:isa,
			key:isa?"0":'',
			clip:clip,
			keys:isa?{}:Object.keys(p.value).reduce(function(a,b){a[b]=true;return a;},{}),
			file:{}
		},
		width:300,
		align:"top:100%",
		root:$o,
		bound:1,
		nose:"top",
		screen:"rgba(255,255,255,0)",
		close:false,
		esc:true,
		enter:true
	})
	.then(function(data){
		var i, cmd = data.cmd,
				redraw = true, 
				keys=[], vals=[], ins;

		if (data.clip.type && data.type=="clip") sample.clip = data.clip.value; 

		ins = Object.clone(data.type=="file"?data.file:sample[data.type],true);

		if (!data.isarray) {
			if (data.type=="string" && data.file.data) ins="data:"+data.file.content_type+";base64,"+data.file.data;
			else delete ins.name;
		}

		if (p.level==1 && /^(_id|_rev)$/.test(data.key)) {
			cw.lib.note("Can’t change system properties.","error");
			return;
		}

		// Object
		if (/^(ob)/.test(t)) {

			// Append/replace is simple
			if (cmd=="append" || p.value[data.key]!=null) { 
				p.value[data.key] = ins; 
			}

			// Prepend is more complex
			else if (cmd=="prepend") {

				keys = Object.keys(p.value);
				for (i in keys) {
					vals.push(p.value[keys[i]]);
					delete p.value[keys[i]];
				}
				p.value[data.key] = ins;
				for (i in keys) {
					if (keys[i]!==data.key) p.value[keys[i]] = vals[i];
				}
				vals=keys=null;
			}
			//Unknown cmd
			else redraw = false;
		}

		// Array
		else if (isa) {
			p.value.insert(ins, data.key);
		}
		else redraw = false;

		if (redraw) {
			// force redraw
			mn.ShowItems = null;
			mn.List = that.MakeList(p);
			p.state=2;
			$f.find(".idesc:eq(0)").trigger("check");
			$f.find(".ilist:eq(0)").trigger("recalc");
		}
	})
	.always(function(){
		$b.parent().removeClass("hover");
		$f.removeClass("hover");
	});
})

