			p = $f.data('my').data,
			mn = $f.data('my').manifest;
	$b.parent().addClass('hover');
	$f.addClass('hover');
	$b.modal({
		manifest:that.MTakeout,
		data:{
			json:JSON.stringify(p.value,null,'  '),
			path:p.path,
			key:p.key,
			replace:Object.size(p.value)?'Replace':'Save',
			extend:Object.size(p.value)?'Extend':''
		},
		width:($f.width()-160).clamp(500,1000),
		align:'top:103%;right:-76px;',
		//root:$o,
    root:$o.find('section#editor').eq(0),
		nose:'top',
		screen:'rgba(255,255,255,0)',
		close:true,
		esc:true,
		enter:true
	})
	.then(function(res){
		var redraw = true,
				res1;

		if (that.my.parent() && that.my.parent().data.isNew) res1 = res.node;
		else res1 = p.level == 0 ? Object.reject(res.node,/^(_id|_rev)$/) : res.node;

		if (res.cmd == 'extend') {
			Object.merge( p.value,  res1, true);
		}
		else if (res.cmd == 'replace') {
			var temp = {};
			if (p.level == 0 && p.value._rev) temp = Object.select(p.value, /^(_id|_rev)$/);
			that.RT('Lib').kick(p.value, res1);
			if (p.level == 0) Object.merge(p.value, temp);
		}
		else redraw = false;

		if (redraw) {
			// force redraw
			mn.ShowItems = null;
			p.state=2;

			that.RT('Lib').relist($f, mn);
		}

	})
	.always(function(){
		$b.parent().removeClass('hover');
		$f.removeClass('hover');
	});
})

