					$o.my("find",".cw-att-upload>span")
					.text(f.name.truncate(15,"middle","…")+" "+a.length.metric(2,false)+"b");

					if (d.key == f.name) {
						$o.my("find","#nkey").val(d.key).trigger("blur");
					}
