({
	data:{text:'', mode:'auto', commitKeys:[], cancelKeys:[]},
	init:[
		'<div style="width:calc(100% - 80px);" class="icmframe dib vat mt-1 mb-1">',
			'<textarea id="ied" style ="width:100%;max-height:300px;" class="xgray"></textarea>',
		'</div>',
		'<div class="buttons w80 dib vat tar o100 pr3 pt0">',
			'<button id="btn-change" class="button green w36" title="Stash property as clip"><span class="fi-check"></span></button>',
			'<button id="btn-discard" class="button w36" title="Del property"><span class="fi-minus-circle"></span></button>',
		'</div>'
	],
	ui:{
		'#ied':{
			init: function($o){

				var that = this,
						d = that.data,
						t = d.text,
						mode = d.mode,
						isNumber = mode=='number',
						start,
						wrap = false,
						lnums = true;

				// Simple heuristics to guess mode
				if (mode == 'auto') {
					start = t.substr(0,800);
					if ((start.match(/(var\s|const\s|function[\s\(]|return\s|[\s\t]if\s)/g)||[]).length > 2) {
						mode = "text/javascript";
						var maxl = 0;
						t.lines().forEach(function(e,i){
							if (i>200) return;
							if (e.length > maxl) maxl = e.length;
						});
						if (maxl>150 && maxl<1000) wrap = true;
					}
					else if (
						/<\/(div|span|p|a|script|head|title|svg|path)/.test(start)
						|| /<(svg|path|group)\s/.test(start)
					) {
						mode = 'text/html';
						wrap = true;
					}
					else if (CodeMirror.modes.erlang && /^[\r\n\s\t]*fun[\r\n\s\t]*\([\r\n\s\t]*\{/.test(start)) {
						mode = 'erlang';
						wrap = true;
					}
					else {
						mode = 'text';
						if (!/[\r\n]/.test(start)) {
							wrap = true;
							if (t.length < 800) lnums = false;
						}
					}
				}

				// Special modes
				if (mode == 'number') {
					mode = "text/javascript";
					lnums = false;
				}
				else if (mode == 'b64') {
					mode = 'text/plain';
					wrap = true;
					lnums = false;
				}

				CodeMirror.fromTextArea($o[0], {
					mode: 					mode,
					viewportMargin:	100,
					lineNumbers:		lnums,
					indentWithTabs:	!0,
					indentUnit:			2,
					tabSize:				2,
					theme: 					'default',
					autofocus:			true,
					lineWrapping:		true, //wrap,
					extraKeys:$.extend(
						{
							'Esc':_cancel,
							'Ctrl-Enter':_commit.debounce(20),
							'Cmd-Enter':_commit.debounce(20)
						}, 
						!isNumber?{}:{'Enter':_commit},
						d.commitKeys.reduce(function(a,b){ a[b] = _commit; return a;}, {}),
						d.cancelKeys.reduce(function(a,b){ a[b] = _cancel; return a;}, {})
					)
				});

				// - - - -
				function _commit(){that.my.trigger('#ied','editorcommit.my')}
				function _cancel(){that.my.trigger('#ied','editorcancel.my')}
			},
			bind:'text'
		},
		'#btn-change':{
			bind:function(d,v){if(v!=null) this.my.trigger('#ied','editorcommit.my')}
		},
		'#btn-discard':{
			bind:function(d,v){if(v!=null) this.my.trigger('#ied','editorcancel.my')}
		}
	}
})

