({
	id:"Sys.JSON.Base64",
	CM:'b64',
	F:{ /* mount on root app init */  },
	init: function($o, form){
		var sig, that = this, 
				d = form.data;
		$o.html(that.HTML);

		if (!d.sig) {
			var trimmed = d.value.replace(/^data:image\/(jpeg|png|gif);base64,/,'');
			d.sig = sig = window.atob(trimmed.to(40));

			d.size = (trimmed.length/4*3).floor();

			if (sig.to(4)==="ÿØÿà") { 
				d.mime =  'image/jpeg'; 
			}
			else if (sig.to(4)==="\u0089PNG") { 
				d.mime = 'image/png'; 
			}

			if (d.mime) {
				// we have mime so its an image
			} else {
				// try to guess it by name
				if (d.path.length==3 && d.path[1]=="_attachments") {
					// we have couchdb doc
					var i, 
							ico="fi-paperclip", 
							mime="application/octet-stream", 
							fname = d.path[2];
					for (i in that.Mime) if (that.Mime[i].test(fname)) mime = i;
					for (i in that.Ico) if (that.Ico[i].test(fname)) ico = i;
					d.mime = mime;
					d.ico = ico;
					d.fname = fname;
				}
			}

			that.RT('Blobs')[d.hash] = URL.createObjectURL(
				$.my.f.base642blob(d.value.replace(/^data:image\/(jpeg|png|gif);base64,/,''), null, d.mime)
			);
		}

	},
	ui:{
		".ikey":{ /* mount on root app init */ },
		".ivalue":{
			init: function($o, form){
				var that = this,
						d = that.data;
				that.F.initEdit($o, that);
			},
			bind:function(d, v, $o){
				var h="", 
						blobs = this.RT('Blobs'),
						img = /^image/.test(d.mime+"") && d.value.length < 5e5?blobs[d.hash]:null;
				if (d.state != 2) {

					var w = $o.width();

					if (img) {
						h+='<a href="'+img+'" target="_blank" class="base64image"><img src="'+img+'" '
						+'class="fl mr10"/></a>';
						w=w-80;
					} 
					else if (d.mime && d.ico && d.fname){
						h+='<a href="' + blobs[d.hash] + '" '
							+' class="base64ico w55 h55 tac dib vat mr10 lgray fs200 bg-lgray br2" '
							+ 'download="'+d.fname+'" '
							+' title="Download '+d.fname+'" '
						+' style="line-height:55px"><span class="'+d.ico+'"></span></a>';
						w = w-80;
					}

					var trim = ((w/10*2.5)|0).clamp(140,600);

					h+='<div class="dib vat base64string">';
					h+='"';
					if (d.value.length>trim) {
						h+=d.value.truncate(trim)
						+'"&nbsp;<a href="'+blobs[d.hash]+'" '
						+'target="_blank" class="ilabel tdn">'
						+(d.mime?'Open':'Download')
						+'&nbsp;'+d.size.metric(2)+'b</a>';
					}
					else h+=d.value+'"';
					if (img) h+='</div>';
					return h;
				}
				else {
					if (v!=null) d.value = v;
					return d.value;
				}
			},
			events:"input.my"
		}
	},
