({
	id:"Sys.JSON.Number",
	CM:'number',
	F:{ 
		N2S: function(v){
			var h = '<span class="orange">'+v.toString()+'</span>';
			if (v<1e13&&v>8e11&&v===(+v).floor()) {
				h+=' <span class="ilabel xgray lh140 vat mt4"><span class="fi-clock"></span> '
				+Date.create(v).format('{d} {Mon} {yyyy}, {H}:{mm}:{ss}, Local')
				+'</span>';
			}
			return h;
		},
		Calc: function(s, prev){
			if (!isNaN(s)) return +s;
			var n = null;
			try {
				n = eval('('+s+')');
			} catch (e) {}
			if (n!==null && !isNaN(n)) return +n;
			return prev;
		}
	},
