manifest.init:
function ($o,form) {
	var i, j, that = this, 
			d = form.data.doc,
			items = d.items;

  // Mount Inliner
  that.Types.rtf.extend.Inliner = $.extend(true, {}, $.my.cache('cw.Inliner') || {});

	$o.html(that.HTML);

	// Parser
	//if (!window.HTMLParser)
	that.HTMLParser(that);

	// CM-related stuff
	that.InitCM();

	// Busy
	if (!that.app) that.app = {};
	if (!that.app.busy) {
		that.app.busy = function(a){
			$o.toggleClass("cw-app-busy", !!a);
		}
	}

	// prepare doc if it’s empty
	if (!d.items.length) {
		d.items.push( 	$.extend(true, {},that.Item.data, {id:that.GenId()}) );
	}

	// extend runtime
	that.AddItem.Typelist = Object.keys(that.Types).remove("attachment");

	// correct doc
	if (d.s3) that.S3toCouch(d);

	// restore tabs
	if (Object.isArray(d.tabs) && d.tabs.length) {
		var tabs = cw.lib.a2o(d.tabs);
		d.items.forEach(function(elt){
			var p = that.Path(elt);
			if (tabs[p]) tabs[p] = elt;
		});

		d.tabs.forEach(function(k,i) {
			var t = tabs[k];
			if (
				Object.isObject(t)
				&& t.id
				&& (
					(t.state && t.state.savedSdbm != t.state.sdbm)
					|| !i
				)
			) {
				that.Tablist.push(t);
			}
		});
		if (that.Tablist[0]) that.List.push(that.Tablist[0]);
		that.Active = that.List[0].id;
	} 


	// Expanded branches restore

	if (Object.isObject(d.expanded)){
		that.Expanded = $.extend(!0, true, d.expanded);
	}


	// If stiil nothing to open – open first item

	if (!that.List.length) {
		that.Active = d.items[0].id;
		that.List.push(d.items[0]);
		that.Tablist.push(d.items[0]);
	}


	// Dup searcher
	that.getDup = function (item, name0) {
		var i,e, ename, fname = that.Path(item, name0), u=false;
		for (var i=0;i<d.items.length && !u; i++) {
			e = d.items[i];
			ename = that.Path(e);
			if (e.id!==item.id && ename===fname) u = e.type;
		}
		return u;
	};

	//remember mimes
	for (i=0;i<d.items.length;i++) {
		j = d.items[i];
		if (j.type==="attachment" && !j.mime) {
			j.mime = d._attachments[(j.folder?j.folder+"/":"")+j.name+"."+j.ext].content_type;
		}
		if (!j.state) j.state = {savedSdbm:"",sdbm:"",depends:{},impacts:{}};
		delete j.savedSdbm; delete j.depends;
	}

	// Prepare Inliner

	var Rtf = that.Types.rtf.extend;
	Rtf.Inliner.T.img.Opts.style[' .tag-in-fmt'] =
	Rtf.Inliner.T.embed.Opts.style[' .tag-in-fmt'] =
	Rtf.style[' .tag-in-fmt'] = $.extend(true,{}, Rtf.Inliner.style[' .tag-in-fmt']);
	//Rtf.Inliner.Ed.doc.style = $.extend(true,{}, Rtf.Inliner.Ed.Theme);

	// 'delete item' event handler

	$o.on("delitem.my", function(evt, item){
		var ipos = d.items.findIndex(_find),
				lpos = that.List.findIndex(_find),
				fname = that.Path(item);
		if (d.items.length>1) {

			if (item.type=="rtf" || item.type=="inliner") {
				// del sub images
				d.items.remove(_findDeps);
				that.List.remove(_findDeps);
				that.Tablist.remove(_findDeps);
			}

			d.items.removeAt(ipos);
			that.List.removeAt(lpos);
			that.Tablist.removeAt(that.Tablist.findIndex(_find));
			if (d._attachments && d._attachments[fname.from(1)]) 
				delete d._attachments[fname.from(1)];
			that.Expanded[item.id] = false;

			if (!that.Tablist) that.Tablist.push(d.items[0]);

			if (!that.List.length) that.List.push(that.Tablist[0]);

			that.Active = that.List[0].id;
			$o.find("#items").trigger("recalc");
		}
		else {
			(window.l||cw.lib).note("Can’t remove last resource.","error");
		}

		function _find (e) { return e.id == item.id; }
		function _findDeps (e) { return (e.type=="attachment" && e.folder=="/inliner" && e.name.startsWith(item.id+"/"));}
	});

}

