#ilist.bind:
function (d,v,$o) {
		var that = this, 
				i, e, html="",
				list = d.doc.items, 
				res = [], fl = "",
				isSearch = that.Search && that.Search.length>1;

		// sort list
		list = list.sortBy(function(e){
			return [
				e.folder.to(1),
				(!!e.folder.from(1)) *1,
				e.folder,
				e.snippet*1,
				e.name,e.type
			].join("/").toLowerCase();
		});

		if (isSearch) {
			// filter list
			var re = new RegExp (that.Search.split(" ").compact(true).map(RegExp.escape).join("|"), "i");
			list = list.filter(function(e){
				return re.test(e.name) || re.test(e.folder) || re.test(e.data);
			});
		}

		// gen items/folders final list
		for (i=0; i<list.length; i++) {
			e = list[i];
			if (e.folder && e.folder != fl) {
				fl = e.folder;
				res.push({
					id:fl,
					html: '<span class="dib w15 gray">'
						+(that.Expanded[fl] || isSearch?"▾":"▸")
						+'</span>'
						+(e.folder=="/"
							?'<span class="fi-folder o50 blue mr4 "></span> <b class="fs105 o90">Attachments</b>'
							:e.folder=="."
							?'<span class="fi-align-left o50 blue mr4 "></span> <b class="fs105 o90">Doc root</b>'
							:fl.to(1)+"&thinsp;"
						)
						+fl.from(1),
					css:"xgray",
					type:"folder"
				});
			} 
			if (isSearch || (!e.folder || that.Expanded[e.folder])) {
				var isdirty = (!e.state.savedSdbm || e.state.sdbm == "Z"  || (e.state.sdbm!==e.state.savedSdbm));
				res.push({
					id:e.id,
					html: '&nbsp;'
						+ (e.parserActive?'<span class="fs90">></span> ':"")
						+ e.name
						+(e.ext?"."+e.ext:"")
						+ (isdirty?'<span class="lgray">&nbsp;•</span>':"")
						+ (e.type==="attachment"?' <span class="fi-paperclip blue fs90 o80"></span>':''),
					css: (e.external?"orange o80":(e.snippet || e.type==="attachment")?"gray":"xgray") 
						+ (e.id == that.Active?" bg-active":""),
					type:e.snippet?"snippet":"item",
					title: e.folder+"/"+e.name+(e.ext?"."+e.ext:""),
					folder: e.folder
				});
			}
		}

		// render list
		for (i=0;i<res.length;i++) {
			e = res[i];
			html+='<div class="siteitem w200 oh '+e.css+(e.folder?" pl15":"")
				+'" id="sx-'+e.id+'" title="'
				+(e.title?e.title:"")+'">'
				+e.html
				+'</div>';
		}
		return html;
	}

