#btn-publish.bind:
function (d,v,$o) {
		var i, async = false,
				note = cw.lib.note,
				that = this,
				rows, key,
				err = {}, atts2go,
				errors={},
				$root = $o.my().root,
				oldhtml = $o.html(),
				proc;
		if (v!=null) {

			proc = $.Deferred();
			$o.html('<span class="dib vat cw-busy w50 h8 br100 mt5"></span>');
			$o.attr("disabled", true);
			proc.then(
				function(msg, skipSave){
					cw.lib.note(msg || "Published to CouchDB","ok");
					if (!skipSave)  {
						d.doc.stamp = Date.now();
						$o.trigger("commit");
					}
				},
				function(txt, msg, stack){
					console.log(msg, stack);
					cw.lib.note("Can’t build doc. "+txt,"error");
				}
			).always(function(){
				$o.attr("disabled", false);
				$o.html(oldhtml);
				that.CloseTabs(function(e){return e.folder=="/inliner" && e.type=="attachment"});
				$o.my().root.my("redraw");
			});

			//-------

			that.Build(d.doc, {
				buttons:true, 
				footer:"Above items won’t be included.",
				checkcreds:true
			}, $o)
			.then(function(a){
				atts2go = a;
				_getEnv ();
			})
			.fail(function(txt, msg, stack){
				proc.reject(txt, msg, stack);
			});
		}

		// - - - - - - -

		function _getEnv () {

			if (!Object.size(atts2go.upload)) {
				if (!Object.size(atts2go.err)) proc.resolve ("All files are up to date, nothing to upload.", true);
				else proc.reject ("All files, scheduled to upload, have errors. Upload cancelled.", "error");
			} else {

				that.db.get(d.doc._id, {attachments:true})
				.then(
					function(odoc){

						if (!odoc) {
							proc.reject("Doc must be saved at least once before publishing.", "error");
							return;
						}

						var ra, na, 
								r = atts2go.result,
								u = atts2go.upload;

						if (Object.size(odoc._attachments)) {
							if (!r._attachments) atts2go.result._attachments = {};
							ra = atts2go.result._attachments;
							na = odoc._attachments;
							Object.keys(na).forEach(function(i){
								if (!ra[i] || null==ra[i].data) {
									ra[i] = {
										content_type:na[i].content_type,
										data:na[i].data
									};
									u["/"+i] = cw.lib.sdbm(na[i].digest);
								}
							});
						}

						_go()
						.then(function(msg,req){
							for (var i=0;i<d.doc.items.length; i++) {
								var item = d.doc.items[i];
								if (u[that.Path(item)] || item.snippet) {
									var ssdbm = item.state.sdbm!="Z" ? item.state.sdbm : u[that.Path(item)];
									item.state.savedSdbm = item.state.sdbm = ssdbm;
								}
							}
							proc.resolve();
						})
						.fail(function(e,req){
							proc.reject("Failed to publish, see console",e,req);
						});
					},

					function (){
							proc.reject("Doc must be saved at least once before publishing.", "error");
					});				
			}
		}

		// - - - - - - - 

		function _go(doc){
			var pi = $.Deferred();
			try {
				that.Upload(d.doc, $o.my().root, atts2go)
				.then(function(msg) {
					pi.resolve(msg);
				})
				.fail(function(e,req){
					pi.reject(e,req);
				});
			} catch (e){
				pi.reject(e.message);
				console.log(e);
			}
			return pi.promise();
		}

	}

