js.s2o:
function (src) {
		var o, err1, err2, ast;
		// First, try eval assuming we have obj or function 
		// inside our source
		try {
			o = eval("("+src+")");//JSON.parse(s);
		} 
		catch (e) { err1 = e; }

		if (err1) {
			// if eval fail, try Uglify, and if it parses – return source
			try {
				ast = UglifyJS.parse(src);
			} 
			catch (e) { err2 = e; }
			if (!err2) return src; 
			throw err2;
		}

		if (err1) throw err1;

		return o;
	}

