#btn-import.bind:
function (d,v,$o) {
		if (v!=null) {
			var that = this, i, j, url,
					obj = that.couch || that.RT().GetSettings("couch"),
					couch = obj.dburl.replace(/\/$/,'')+'/',
					creds = (obj.login && obj.pwd) ? obj.login +":" + obj.pwd :'',
					id = obj.docid,
					headers = {Accept:"text/plain"};

			url = couch+id;
			if (creds) headers.Authorization = "Basic "+ cw.lib.base64(creds);

			$o.my().root.addClass("busy");

			$.ajax({
				url:url+'?attachments=true',
				method:"GET",
				cache:false,
				dataType:"json",
				headers:headers
			})
			.then(function(nd){

				// merge
				var i, 
						nda = nd._attachments,
						od = d.items,
						oda = nd._attachments && !d._attachments?(d._attachments={}):d._attachments,
						nogo = {},
						ctr = od.length;
				// existing
				od.forEach(function(e){nogo[that.Path(e)]=true});

				$o.my().root.removeClass("busy");


				//enum nodes
				var ext = [], 
						iter = function(e, node, start) {
							var n1 = node || nd, 
									os = start ?Object.clone(start,true):({folder:"."});
							if (!nogo[(os.folder!="."?os.folder:"")+"."+e]) {
								if (node || !Object.isObject(n1[e])) {
									os.name = e;
									if (Object.isString(n1[e])) {

										var n2 = cw.lib.unjson([n1[e]]);
										if (!Object.isString(n2[0])) {
											ext.push($.extend(true, os, {
												type:"json",
												data:"("+cw.lib.js2txt(n2[0],"  ")+")",
												mime:"application/json"
											}));
										}
										else ext.push($.extend(true, os, {
											type:"txt",
											data:n1[e],
											mime:"text/plain"
										}));
									}
									else ext.push($.extend(true, os,{
										type:"json",
										data:"("+cw.lib.js2txt(cw.lib.unjson(n1[e]),"  ")+")",
										mime:"application/json"
									}));

								} else {
									Object.keys(n1[e]).forEach(iter.fill(void 0, n1[e], {folder:"."+e}));
								}
							}
						};

				Object.keys(Object.reject(nd,/^_/)).forEach(iter.fill(void 0,false,{folder:"."}));

				ext.forEach(function(obj) {
					var ni = $.extend(!0,{}, that.RT().Item.data, {id:that.RT().GenId()});
					$.extend(ni, obj);
					od.push(ni);
				});

				//enum new atts
				Object.keys(nda||{}).forEach(function(i){

					if (!nogo['/'+i]) {

						var n = nda[i],
								nm = n.content_type, 
								ni = $.extend(!0,{}, that.RT().Item.data, {id:that.RT().GenId()});

						ni.type="attachment";
						ni.mime = nm;
						if (!i.has("/")) ni.name = i;
						else {
							var a = i.split("/");
							ni.folder = "/"+a[0];
							ni.name = a.slice(1).join("/");
						}

						// look for editable attaches 
						if (nm == "text/plain") ni.type="txt";
						else if (/\/javascript$/i.test(nm)) ni.type="js";
						else if (/\/json$/i.test(nm)) ni.type="json";
						else if (/\/css$/.test(nm)) ni.type="css";
						else if (/\/[x]?htm[l]?$/.test(nm)) ni.type="html";

						if (ni.type!="attachment") {
							ni.data = cw.lib.base64(n.data, true);
						} else {
							// Put att data to _att
							oda[i] = {
								content_type:nm || "application/octet-stream",
								data:n.data
							}
						}

						od.push(ni);

					}
				});

				if (od.length> ctr) {
					cw.lib.note("Fetched "+(od.length - ctr)+" nodes. Click Apply button to merge with the project.","ok", 5000);
				}

			})
			.fail(function(){
				$o.my().root.removeClass("busy");
				cw.lib.note("Couldn‘t fetch doc to import.","error");
			})
		}
	}

