#cprgb.bind:
function (d,v0,$o) {

		var parsed= false, rgba, v=v0;
		if (!this.InitRgb) {
			v = d.css;
			this.InitRgb = true;
		}
		if (v!=null) {
			if (/^(#[0-9a-f]{3}|#[0-9a-f]{6})$/i.test(v)) {
				if (v.length==4) {
					rgba = [
						parseInt(v[1]+v[1],16),
						parseInt(v[2]+v[2],16),
						parseInt(v[3]+v[3],16),
						1
					];
				} else {
					rgba = [
						parseInt(v[1]+v[2],16),
						parseInt(v[3]+v[4],16),
						parseInt(v[5]+v[6],16),
						1
					];
				}
				parsed=true;
			}
			else if (/^rgb[a]?\(\s*\d{1,3}\s*,\s*\d{1,3}\s*,\s*\d{1,3}\s*(,\s*(0(\.\d+)?|1)\s*)?\)\s*$/.test(v)) {
				var rgba = v.split("(")[1].split(")")[0].replace(/\s/g,'').split(",");
				if (rgba.length!=4) rgba[3]=1;
				parsed = true;
			}

			if (!parsed) return v;
			else {
				d.rgba = rgba.slice(0);

				d.hsb = this.Rgb2hsb(d.rgba);

				if (rgba[3] == 1) {
					d.css = "#"+rgba.to(3).map(function(e){
						return e.toString(16).padLeft(2,"0");
					}).join("").toUpperCase();
