ColorPicker.Rgb2hsb:
function (a) {

	var hsb=[],
			c = this.Color,
			min = Math.min(a[0],a[1],a[2]),
			max = Math.max(a[0],a[1],a[2]),
			sat, br;
	br = (100*max/255);

	if (br==0) sat=0;
	else sat = (100*(max-min)/max);

	if (sat==0) hsb[0]=0;
	else {
		var k = 255/(max-min),
				r = k*(a[0]-min),
				g = k*(a[1]-min),
				b = k*(a[2]-min),
				h=0;
		if (r>=254.99) h= +g-b;
		else if (g>=254.99) h = 510+b-r;
		else if (b>=254.99) h = 1020+r-g;
		hsb[0] = ((360+(h*360/1530))%360).round();
	}
	hsb[1] = sat.round();
	hsb[2] = br.round();
	return hsb;

		}

manifest.Build:
function (doc0,opts0,$o) {

	var i,j,that = this,
			d = that.data,
			pi = $.Deferred(),
			tdoc = doc0 || that.data.doc,

			opts = $.extend(!0,{ 
				buttons:true, 
				footer:"", 
				checkcreds:false,
				pick:"",
				resolve:true,
				silent:false,
				blocking:true
			},Object.reject(opts0||{},"errors")),

			errors = (opts0||{}).errors || {},
			key,
			items={},
			_process;

	for (i=0;i<tdoc.items.length;i++) {
		if ( !tdoc.items[i].snippet && (j=that.Path(tdoc.items[i])).to(1) != ".") {
			items[j.from(1)] = tdoc.items[i];
		}
	}

	// Make app busy
	if (that.app && that.app.busy) {
		pi.always(function(){ that.app.busy(false); });
		that.app.busy(true);
	}

	// Decode / prepare creds
	var couch = that.GetSettings("couch");

	if (null==couch.docid) {
		if (opts.checkcreds) pi.reject("Can’t decode encrypted doc _id and credentials.");
	}

	_process = _Process.debounce(100);

	try {
		if (opts.resolve) that.ResolveExternals(tdoc, errors).always(_process);
		else _process();

	} catch(e) {
		pi.reject("Error during validation", e.message, e.stack);
	}

	return pi.promise();


	//- - - - - - - - - - - - - - -

	function _Process (){
		var a = that.Items2Atts(tdoc, that.Types, errors, opts.pick?opts.pick:true);

		if (couch.docid) a.result._id = couch.docid;

		if (Object.size(a.err)) {
			// Remove busy shield
			if (that.app && that.app.busy) that.app.busy(false);
			// Ask user what to do
			$o.modal({
				manifest:that.ParseErrors,
				data:$.extend(!0, {}, opts,a ),
				esc:true,
				enter:true,
				screen:opts.blocking?'rgba(255,255,255,0.5)':false,
				align:"top:105%",
				nose:"top",
				bound:10,
				drag:!opts.blocking
			})
			.then(function(){
				_result(a);
			})
			.fail(function(){
				pi.reject("Cancelled");
			});
		} 
		else {
			if (!opts.silent) cw.lib.note("Doc integrity seems to be Ok.");
			_result(a);
		}
	}


	//- - - - - - - - - - - - - - -

	function _result(a){
		if (a.result) {
			var att  = a.result._attachments;

			// Make it last node of the doc
			delete a.result._attachments;
			a.result._attachments = att;

			//console.log(a);

			// load binary atts
			that.db.get(d.doc._id, {
				attachments:true
			})
			.then(
				function(res){
					if (res._attachments) for (var i in res._attachments) {
						if (!a.result._attachments[i] || null == a.result._attachments[i].data) {
							a.result._attachments[i] = {
								content_type:res._attachments[i].content_type,
								data:res._attachments[i].data
							}
						}
						if(items[i]) items[i].state.crc = $.my.f.sdbmCode(res._attachments[i].data);
					};
					pi.resolve(a);
				},
				function(){
					if (!opts.silent) cw.lib.note("Doc is not yet saved, you may need to save doc.", "warning");
					pi.resolve(a);
				}
			);
		}

	} // --end _result

}

manifest.ResolveExternals:
function (doc0,errs) {
	var that = this, 
			pis,
			async,
			pending = {},
			doc = (doc0 || that.data.doc),
			rows = doc.items,
			z = $.Deferred(),
			hasErrors = false,
			err = errs || {},
			oitems = {};

	rows.forEach(function(e){oitems[e.id] = e});

	pis = rows.map(function(e,i){
		var s="", id='', p = e.external, pi; 
		if (p && !/^[x\s]/.test(p)) {
			async = true;
			pi = $.Deferred();

			// Local doc
			if (p.startsWith("pouch://") || p.startsWith("local://")) {
				p = p.from(8).split(/\s+/).compact(true);
				s = p[1]||'';
				id = p[0];

				if (!pending["pouch "+id]) pending["pouch "+id] = that.db.get(id, {attachments:true});

				pending["pouch "+id].then(
					function(doc) {
						if (s) {
							var r = cw.lib.getref(doc, s);
							if (r) {
								pi.resolve(r);
							} else {
								pi.reject("Failed to process local doc '"+id+"', property '"+s+"' not found");
							}
						} else {
							if (doc) pi.resolve(doc);
							else pi.reject("Failed to load local doc '"+id+"'");
						}
					},
					function(){ pi.reject("Failed to load local doc '"+id+"'"); }
				);
			}

			// Local doc, other DB
			else if (p.startsWith("query://")) {
				p = p.from(8).split(/\s+/).compact(true);
				s = p[1]||'';

				// check if query has db
				var db,
						q = p[0].split("/").to(3);
				if (q.length < 2) pi.reject('Too short path for query');
				else {
					db = q.length==2?that.db:cw.db(q[0]);
					if (db) {
						var query = q.last(2).join("/"),
								params,
								err;
						try {
							params = eval (s?"("+s+")":'');
							if (params !== undefined && !Object.isObject(params)) err = "Params must be an object";
							else params = params || {};
						} catch(e){
							err = "Invalid params for query";
						}
						if (!err) {

							if (!pending["doc "+p[0]]) {
								pending["query "+p.join(" ")] = db.query(query, params);
							}
							pending["query "+p.join(" ")].then(
								function(res) {
									pi.resolve(res.rows);
								},
								function(){ pi.reject("Query '"+p.join(" ")+"' failed"); }
							);

						} else {
							pi.reject(err);
						}
					} else {
						pi.reject("PouchDB DB '"+q[0]+' does not exist' );
					}
				}
			}

			// Local doc, other DB
			else if (p.startsWith("doc://")) {
				p = p.from(6).split(/\s+/).compact(true);

				s = p[1]||'';
				id = p[0];

				var dbid = id.split("/",1)[0];
				id = id.from(dbid.length+1);

				if (cw.db(dbid)) {

					if (!pending["doc "+p[0]]) {
						pending["doc "+p[0]] = cw.db(dbid).get(id, {attachments:true});
					}

					pending["doc "+p[0]].then(
						function(doc) {

							if (!doc) {
								pi.reject("Failed to load PouchDB doc '"+p[0]+"'");
							}

							else if (s) {
								var r = cw.lib.getref(doc, s);
								if (r) pi.resolve(r);
								else pi.reject("Failed to process PouchDB doc '"
															 +p[0]+"', property '"+s+"' not found");
							} else {
								pi.resolve(doc);
							}
						},
						function(){ pi.reject("Failed to load PouchDB doc '"+p[0]+"'"); }
					);
				} else {
					pi.reject("PouchDB DB '"+dbid+' does not exist' );
				}
			}

			// External request
			else if (/^http[s]?:\/\//.test(p)) {
				p = p.split(/\s+/).compact(true);
				s = p[1]||'';
				id = p[0];

				if (!pending["url "+id]) {

					if ('https:'==window.location.protocol && !id.startsWith(window.location.protocol)) {
						pending["url "+id] = $.Deferred().reject('forbidden protocol').promise();
					}
					else 	pending["url "+id] = $.ajax({
							url:id,
							cache:false,
							type:"GET",
							dataType:s?"json":"text",
							//xhrFields:{withCredentials:/^https/.test(id)}
						});
				}

				pending["url "+id].then(
					function(doc) {
						var json, r;
						if (s) {
							if (!Object.isObject(doc) && !Object.isArray(doc)) {
								try { doc = JSON.parse(doc) } catch (e) { }
							}
							r = cw.lib.getref(doc, s);
							if (r) {
								pi.resolve(r);
							} else {
								pi.reject("Failed to process external doc '"+id+"', property '"+s+"' not found");
							}
						} else {
							if (doc) pi.resolve(doc);
							else pi.reject("Failed to load external doc '"+id+"'");
						}
					},
					function (msg){ pi.reject("Failed to load external doc '"+id+"'" + (Object.isString(msg)?': '+msg:'')); }
				);
			}

			// Unknown, reject
			else {
				pi.reject("Failed to load resource "+id+', protocol is unknown.');
			}

			return pi.promise();

		} else {
			return e.data;
		}
	});

	if (!async) z.resolve();
	else {
		Q.allSettled(pis)
		.then(
			function(reps){
				reps.forEach(function(elt,i){

					var d = rows[i], 
							aname = that.Path(d),
							fconv,
							convresult,
							val;
					if (!d) {
						// possibly item was removed
						// by converter
						return;
					}
					if (elt.state ==="fulfilled") {
						val = elt.value;

						if (Object.isString(val)) rows[i].data = val;
						else if (Object.isObject(val)) {

							if (val.type && that.X[val.type] && (fconv = that.X[val.type][rows[i].type])){
								// we have converter
								try {  convresult = fconv.call (that, val, d, oitems);  } 
								catch(e) { convresult = e.message; }

								if (convresult) {
									// error
									hasErrors = true;
									_e(aname, "Converter error:" + convresult);
								}
							}
							else if (rows[i].ext=='js') {
								rows[i].data = cw.lib.js2txt(cw.lib.unjson(elt.value),'\t');
							}
							/*else if (rows[i].type=="rtf") {
								if (elt.value.type=="inliner") {
									rows[i].doc = $.extend(!0,{},elt.value,{_id:rows[i].id});
									rows[i].data = "";
								} else {
									// error
									hasErrors = true;
									_e(aname, 'Invalid external doctype, must be "inliner".');
								}
							}*/
							else {
								rows[i].data = JSON.stringify(val, null, '\t');
							}
						} 
						else {
							rows[i].data = JSON.stringify(val, null, '\t');
						}
					}
					else {
						// error, promise rejected
						hasErrors = true;
						_e(aname, elt.reason);
					}

				});


				// apply oitems additions/deletions
				// to doc.items

				var items = [], 
						atts = doc._attachments||{}, 
						done = {};

				doc.items.forEach(function(elt){
					if (oitems[elt.id]) {
						_item(oitems[elt.id]);
						done[elt.id]=true;
					} 
				});

				Object.keys(oitems).forEach(function(i){
					if (done[i]) return;
					if (oitems[i]) _item(oitems[i]);
					else {
						if(doc._attachments) {
							var item = doc.items.find(function(e){ return e.id==i });
							var path = that.Path(item);
							if (path.to(1)=="/") {
								delete doc._attachments[path.from(1)];
							}
						}
					}
				});

				// put updated items list back into doc

				doc.items = items;

				if (!hasErrors) z.resolve();
				else z.reject ("Failed to load external resource(s)","error");

				//- - - - - - - - - - - 

				function _item(item0) {

					//console.log(item0)

					var item = item0;
					if (item) {
						if (item.type=="attachment" && Object.isObject(item.data)) {

							var path = that.Path(item);


							if (path.to(1)=="/") {
								if (!doc._attachments) doc._attachments={};

								doc._attachments[path.from(1)] = Object.select(item.data, ["content_type","digest","data"]);
								item.data = " Ext doc ";
							}
						}
						//delete item.preview;
						//item.random = cw.lib.sdbm(Date.now+"");
            if (!items.find(function(e){return e.id==item.id && e.state.crc == item.state.crc;})) {
							items.push(item);
            }
					}
				}
			},
			function(e){
				z.reject ("Failed to load external resource(s)","error");
			}
		)
	}

	return z.promise();


	//- - - - - - - - - - - - - - - - - - - - - - - -

	function _e (iname, error) {
		// Logs error
		if (!err[iname]) err[iname] = [];
		err[iname].push(error);
	}

}

manifest.Items2Atts:
function (doc0,Types,prevErrors,pick0,depsOnly) {
	var i, j, e, item, 
			that=this,
			doc = doc0 || that.data.doc,
			buildAll = !!pick0,
			pick = Object.isString(pick0)?pick0:"",
			t = Types || that.Types, 
			err = prevErrors || {},

			names = {}, dirty = {},
			aname="", a={}, sn={}, pp={}, tmp = {},
			src = {}, fin = {}, stash = {},

			incls = {}, 
			fvalidator = null, 
			isinvalid = null,
			z={};

	var reSnippet = /([{[]\/\*\{\{[.\/-][.\/_a-z0-9-]+\}\}\*\/[}\]])|("\{\{[.\/-][.\/_a-z0-9-]+\}\}")|(\{\{[.\/-][.\/_a-z0-9-]+\}\})/gi;


	// gen empty res obj
	var couch = that.GetSettings("couch");

	if (couch.docid) z._id = couch.docid;
	if (/^_design\//.test(couch.docid)) z.name = doc.name
	z._attachments = {};

	// gen work structs
	for (i=0;i<doc.items.length;i++) {
		e = doc.items[i];
		if (e) {
			aname = that.Path(e);
			names[aname]=true;
			sn[aname]=e.snippet;
			src[aname]=doc.items[i];
			if (e.stashResolved) stash[aname] = true;
			if (Object.isFunction(t[e.type].postprocess)) pp[aname] = t[e.type].postprocess.bind(that);
		}
	}


	// stringify
	Object.keys(names).sortBy().forEach(_convert);


	// Resolves include directives.
	// Possible syntaxes are:
	// {{.res.name}} 			– inserts as is
	// {/*{{.res.name}}*/} – inserts as is
	// [/*{{.res.name}}*/] – inserts as is
	// "{{.res.name}}" 		– inserts stringified version

	// parse includes
	for (i in a) {
		if (a.hasOwnProperty(i)) _parseIncludes (i);
	}

	// merge snippets deps into files 
	for (i in a) {
		if (a.hasOwnProperty(i) && !sn[i]) {
			e = src[i]; 
			tmp = {};
			for (j in e.state.depends) 
				if (sn[j] && src[j].state.depends) 
					$.extend(tmp, src[j].state.depends);
			$.extend(e.state.depends, tmp);
			//e.state.depends = tmp;
		}
	}

	// merge files deps into files 
	for (i in a) {
		if (a.hasOwnProperty(i) && !sn[i]) {
			e = src[i]; 
			if (e) {
				tmp = {};
				for (j in e.state.depends) {
					if (e.state.depends.hasOwnProperty(j) && a[j] && !sn[j] && src[j].state.depends) {
						$.extend(tmp, src[j].state.depends);
					}
				}
				$.extend(e.state.depends, tmp);
			}
		}
	}

	// clean impacts
	for (i in src) {
		if (src.hasOwnProperty(i)) {
			if (e = src[i]) {
				e.state.impacts = {};
			}
		}
	}

	// rebuild impacts (reverse deps)
	for (i in a) {
		if (a.hasOwnProperty(i)) {
			if (e = src[i]) {
				for (j in e.state.depends) {
					if (e.state.depends.hasOwnProperty(j)) {
						if (src[j]) src[j].state.impacts[i] = true;
						else {
							//console.log("Missing include "+j+" in "+i);
							e.state.depends[j] = false;
							_e(i, "Missing include '"+j+"'")
						}
					}
				}
			}
		}
	}


	if (depsOnly) return {
		err: err,
		doc:doc
	};

	// build list of items to be updated
	var imps = {};
	for (i in a){
		if (a.hasOwnProperty(i) && (!pick || i === pick)) {
			e = src[i];
			if (!!buildAll || e.state.sdbm !== e.state.savedSdbm) {
				$.extend (true, imps, e.state.impacts);
				if (!e.snippet) $.extend (true, dirty, e.state.depends);
				dirty [i] = true;
			}
		}
	}
	// add dependees to dirty list, 
	// they also need to be updated
	for (i in imps) {
		if (imps.hasOwnProperty(i)) {
			e = src[i];
			if (e) {
				if (!e.snippet) $.extend (true, dirty, e.state.depends);
				dirty [i] = true;
			}
		}
	}

	//console.log("Dirties wired to rebuild: ", dirty);

	// build snippets
	for (i in dirty) {
		if (dirty.hasOwnProperty(i) && sn[i]) {
			try {
				_resolveIncludes (i);
			} catch (er) {
				_e(i, "Error resolving snippet include: "+er.message);
			}
		}
	}

	// build files
	for (i in dirty) {
		if (dirty.hasOwnProperty(i) && !sn[i] && src[i]) {
			try {
				_resolveIncludes (i);
			} catch (er) {
				_e(i, "Error resolving file include: "+er.message);
			}
		}
	}

	// validate finalized versions
	for (i in dirty) {
		if (dirty.hasOwnProperty(i) && (!sn[i] || i === pick) && !err[i] && src[i]) {
			fvalidator = null;
			isinvalid = null;
			try {
				fvalidator = (t[src[i].type].manifest || t[src[i].type].extend || {}).validator;
				if (Object.isFunction(fvalidator)) {
					isinvalid = fvalidator ({data:fin[i]});
				}
			} catch (er) {
				_e(i, "Final result is not of type '"+src[i].type+"': "+er.message);
			}
			if (isinvalid != null) _e(i, "Final result is not of type '"+src[i].type+"': "+isinvalid);
		}
	}


	// build attachments and branches
	var temp0;
	function _renderItemFin(i) {
		if (i.to(1) == "/") return t[src[i].type].s2s?t[src[i].type].s2s(fin[i]):fin[i];
		else return t[src[i].type].s2o?t[src[i].type].s2o(fin[i]):fin[i];
	}

	for (i in dirty) {
		if (dirty.hasOwnProperty(i) && !err[i] && src[i]) {

			temp0 = null;

			if (stash[i]) {
				temp0 = _renderItemFin(i);
				stash[i] = temp0; 
			}

			if (!sn[i] || i === pick) {
				try {	

					if (temp0 === null) temp0 = _renderItemFin(i);

					if (i.to(1)=="/") {
						if (src[i].isBinary && Object.isString(temp0) && /^[\u0000-\u00ff]+$/.test(temp0)) {
							a[i].data = window.btoa (temp0);
						} else {
							a[i].data = $.my.f.base64(temp0, false, false);
						}
						src[i].state.crc = $.my.f.sdbmCode(temp0);
						z._attachments[i.from(1)] = a[i];
					}
					else {
						$.extend(true,z, $.my.f.unmask(temp0, i.from(1)));
					}
				}
				catch (er) {
					_e(i, "Include was not updated, error: "+er.message);
				}
			}
		}
	}


	var process={};
	for (i in src) {
		if (src.hasOwnProperty(i)) {

			//if (stash[i] && !err[i] && stash[i]!==true) src[i].resolved = stash[i];
			if (stash[i] && !err[i] && stash[i]!==true) {
				src[i].resolved = (typeof stash[i] == 'string'?stash[i]:JSON.parse($.my.tojson(stash[i])));
			}
			else delete src[i].resolved;

			if (
				src[i].type === "attachment"
				&& (!!buildAll || src[i].state.sdbm !== src[i].state.savedSdbm)
				&& (!pick || i===pick)
			) {
				process[i] = true;

				// May be we have not yet saved attaches
				if (doc._attachments && doc._attachments[i.from(1)] && doc._attachments[i.from(1)].data) {
					z._attachments[i.from(1)] = {
						content_type:doc._attachments[i.from(1)].content_type,
						data:doc._attachments[i.from(1)].data
					}
				}
			}
			if (!sn[i] && dirty[i] && !err[i]) process[i] = true;
		}
	}

	return {
		err:err,
		dirty: dirty,
		doc:doc,
		upload:process,
		result:z
	};


	//- - - - - - - - - - - - - - - - - - - - - - - -

	function _parseIncludes (iname) {
		var i = iname, deps,
				j, re, idx, str, 
				incl = a[i].data.match(reSnippet);

		deps = src[i].state.depends={};
		if (incl && !src[i].noIncludes) {		
			incls[i] = incl.slice(0);
			for (j=0;j<incl.length;j++) {
				idx = incl[j].replace(/(\/\*|\*\/)/gi,"").replace(/[^a-z0-9_\-\.\/]/gi,"");
				deps[idx] = true;
			}
		}
	}


	//- - - - - - - - - - - - - - - - - - - - - - - -

	function _resolveIncludes (iname) {
		var i = iname, 
				j, re, idx, str, 
				incl = a[i].data.match(reSnippet);

		fin[i] = a[i].data;
		if (incl) {			
			for (j=0;j<incl.length;j++) {
				idx = incl[j].replace(/(\/\*|\*\/)/gi,"").replace(/[^a-z0-9_\-\.\/]/gi,"");
				re = new RegExp(RegExp.escape(incl[j]), 'g');
				if (idx === i) fin[i] = fin[i].replace(re,"");
				else if (a[idx]) {
					str = fin[idx] || a[idx].data;
					var s2s=t[src[idx].type].s2s;
					if (Object.isFunction(s2s)) str = s2s(str);

					if (incl[j].to(1) === '"' && incl[j].from(-1) ==='"') str = JSON.stringify(str);
					fin[i] = fin[i].split(re).join(str);
				} 
				//else fin[i] = fin[i].replace(re,"");
			}
		}
		// post-process
		if (pp[i]) {
			try { fin[i] = pp[i](src[i], fin[i], doc);  }
			catch (e) {
				_e(i, "Post-processor error: "+e.message);
				//console.log(e.message, e.stack, e);
				//window.M = fin[i];
			}
		}
	}

	//- - - - - - - - - - - - - - - - - - - - - - - -

	function _convert (iname) {
		var it=src[iname], att,
				parser, eparser;

		if (it.type!="attachment") {			
			// Item			
			if(it.parserActive) {
				try { 
					parser = eval(it.parser).bind(that); 
				} catch (e){ 
					parser = null; 
					eparser = e.message;
				}
				if (!Object.isFunction(parser)) {
					_e (iname, "Invalid custom parser: "+eparser);
					parser = t[it.type].stringify.bind(that);
				}
			} 
			else parser = t[it.type].stringify.bind(that);

			try {
				att = {
					content_type:it.mime||t[it.type].mime,
					data:parser(it, doc._attachments, doc)+""
				};
			} catch(e) {
				if (it.parserActive) _e(iname, "Custom parser failed: "+e.message);
				else _e(iname,"Error parsing data: "+e.message);
			}
			if (att ) {
				a[iname] = att;
				it.state.sdbm = $.my.f.sdbmCode(Object.reject(it, ["state"]));
			}
		}
		else {
		// We have attachment
			if (doc._attachments && (att = doc._attachments[iname])) {
				if (att.digest) it.state.sdbm = $.my.f.sdbmCode(att.digest);
			} else {
				it.state.sdbm = "Z";
			}
		}
	}

	//- - - - - - - - - - - - - - - - - - - - - - - -

	function _e (iname, error) {
		// Logs error
		if (!err[iname]) err[iname] = [];
		err[iname].push(error);
	}

}

manifest.Upload:
function (doc,$root,opts) {

	var that = this, i, j, url,
			pi = $.Deferred(),
			cdb = that.GetSettings("couch"),
			couch = cdb.dburl.replace(/\/$/,'')+'/',
			creds = (cdb.login && cdb.pwd) ? cdb.login +":" + cdb.pwd :'',
			id = cdb.docid,
			headers = {Accept:"text/plain"},
			mode = cw.lib.a2o(doc["export"].options||[]),
			paths = [],
			obj = opts.result,
			items={},
			exclude = [];

	var isA = Object.isArray, isB = Object.isBoolean, isS = Object.isString, isO = Object.isObject,
			isN = Object.isNumber, isR = Object.isRegExp, isF = Object.isFunction;

	// Append current host root if no domain defined

	if (couch.to(1)=="/") {
		couch = window.location.protocol +"/"+"/" +window.location.host+"/"+couch;
	}

	url = couch+id;
	if (creds) headers.Authorization = "Basic "+ cw.lib.base64(creds);

	for (i=0;i<doc.items.length;i++) {
		if ( !doc.items[i].snippet){
			j = that.Path(doc.items[i])
			if ( j.to(1) != ".") items[j.from(1)] = doc.items[i];
			else paths.push(j.from(1));
		}
	}

	paths = paths.unique().sortBy();

		console.log("LOAD START", url.to(500), headers);

	$.ajax({
		url:url,//+'?attachments=true',
		method:"GET",
		cache:false,
		dataType:"json",
		headers:headers
	})
	.then(_combine,_save);


	return pi.promise();

	//--------------------

	function _combine(doc0) {
		if (doc0) {
			if (doc0._rev) obj._rev = doc0._rev;

			if (mode.extend) {
				var doc1 = Object.reject(doc0,"_attachments"),
						vals = paths.reduce(function(a, path){
							a.push(cw.lib.getref(obj, path));
							return a;
						},[]);

				_unmask(doc1, vals, paths);

				obj = Object.merge(doc1, {_attachments:obj._attachments});

				var a = doc0._attachments;
				if (Object.size(a)) {
					if (!obj._attachments) obj._attachments={};
					Object.keys(a)
					.forEach(function(i){
						if (null==obj._attachments[i]) {
							// no local att, so keep remote att
							obj._attachments[i] = a[i];
						}
						else if (!items[i]) {
							delete obj._attachments[i];
							obj._attachments[i] = a[i];
						}
						else if (items[i].state.crc) {
							// have local, check is it eq to remote
							if (
								items[i].state.remote == items[i].state.crc+"-"+a[i].digest
							) {
								// no need to re-save
								obj._attachments[i] = a[i];
								exclude.push(i);
							} 
							else items[i].state.remote="";
						} 
					});
				}
			}
		}
		_save();
	}

	//--------------------

	function _unmask (dest, src0, mask0) {
		// unfolds masked into obj
		// _unmask ( {x:1, y:{}}, [{z:5},3],["m.n","y.z"]) => {x:1, m:{n:{z:5}, y:{z:3}}},
		// modifies dest

		var mask = mask0, src = src0;

		if (isS(mask)) {
			mask = [mask]; src= [src];
		}
		if (isA(src) && isA(mask)) {
			for (var i=0;i<mask.length;i++) {
				if (src[i]!=null) _blow(dest, src[i], mask[i]);
			}
			return dest;
		} 
		else return null;

		//- - -
		function _blow(data, val, ref) {
			var ptr, path, preptr, i=0;
			if (!/\./.test(ref)) {
				//ref is flat
				if (null!=val) data[ref] = val;
			} else {
				path = ref.split(".").each(function (a,i){this[i]=String(a).compact();});
				ptr = data;
				for (;i<path.length;i++) {
					if (i===path.length-1) ptr[path[i]] = val; //we'r in the hole
					else {
						if (i===0) {
							ptr = data[path[0]];
							preptr= data;
						}
						else {
							preptr = preptr[path[i-1]];
							ptr = ptr[path[i]];
						}
						if (
							null == ptr 
							|| !(isO(ptr) 
									 || !(
										 isA(ptr) 
										 && !isNaN(path[i+1]) 
										 && +path[i]>-1)
									)
						) ptr = preptr[path[i]] = {};
					}
				}
			}
			return data;
		}
	}

	//--------------------

	function _save () {

		console.log("SAVE", url.to(500));

		return $.ajax({
			url:url,
			method:"PUT",
			//dataType:"json",
			//headers:{Accept:"application/json"},
			headers:headers,
			data:$.my.tojson(obj)
		})
		.then(function(res){

			_load();
		})
		.fail(function(e){
			console.log("FAIL", e)
			pi.reject(e);
		});

		/*return $.ajax({
			url:url,
			method:"POST",
			dataType:"text",
			//headers:{Accept:"application/json"},
			headers:headers,
			data:$.my.tojson(obj)
		})
		.then(function(res){

			_load();
		})
		.fail(function(e){
			console.log("FAIL", e)
			pi.reject(e);
		});*/
	}

	//--------------------

	function _load (doc0) {
		console.log("LOAD", url.to(500));
		return $.ajax({
			url:url,
			method:"GET",
			cache:false,
			dataType:"json",
			headers:headers
		})
		.then(function(res){

			// record md5-s
			Object.keys(res._attachments||{})
			.forEach(function(i){
				if (items[i]) {
					items[i].state.remote = items[i].state.crc +"-" + res._attachments[i].digest;
				}
			});

			pi.resolve();
		})
		.fail(function(e){
			pi.reject(e);
		});
	}

}

manifest.GetSettings:
function (section) {
	var that = this,
			key,
			d = that.data;
	// Decode / prepare creds
	if ( cw.crypto && (key = d.doc.creds.crypto)) {
		if (!cw.crypto.has(key)) {
			return {  };
