		function maybe_drop_post(s) {
			var last = s.length - 1;

			if (s[last].type === "dot") {
				return [];
			}
			if (s[last].type === "fun" && s[last - 1].token === "fun") {
				return s.slice(0, last - 1);
			}
			switch (s[s.length - 1].token) {
				case "}":
					return d(s, {
						g: ["{"]
					});
				case "]":
					return d(s, {
						i: ["["]
					});
				case ")":
					return d(s, {
						i: ["("]
					});
				case ">>":
					return d(s, {
						i: ["<<"]
					});
				case "end":
					return d(s, {
						i: ["begin", "case", "fun", "if", "receive", "try"]
					});
				case ",":
					return d(s, {
						e: ["begin", "try", "when", "->",
								",", "(", "[", "{", "<<"
							 ]
					});
				case "->":
					return d(s, {
						r: ["when"],
						m: ["try", "if", "case", "receive"]
					});
				case ";":
					return d(s, {
						E: ["case", "fun", "if", "receive", "try", "when"]
					});
				case "catch":
					return d(s, {
						e: ["try"]
					});
				case "of":
					return d(s, {
						e: ["case"]
					});
				case "after":
					return d(s, {
						e: ["receive", "try"]
					});
				default:
					return s;
			}
		}

		function d(stack, tt) {
			// stack is a stack of Token objects.
			// tt is an object; {type:tokens}
			// type is a char, tokens is a list of token strings.
			// The function returns (possibly truncated) stack.
			// It will descend the stack, looking for a Token such that Token.token
			//  is a member of tokens. If it does not find that, it will normally (but
			//  see "E" below) return stack. If it does find a match, it will remove
			//  all the Tokens between the top and the matched Token.
			// If type is "m", that is all it does.
			// If type is "i", it will also remove the matched Token and the top Token.
			// If type is "g", like "i", but add a fake "group" token at the top.
			// If type is "r", it will remove the matched Token, but not the top Token.
			// If type is "e", it will keep the matched Token but not the top Token.
			// If type is "E", it behaves as for type "e", except if there is no match,
			//  in which case it will return an empty stack.

			for (var type in tt) {
				var len = stack.length - 1;
				var tokens = tt[type];
				for (var i = len - 1; - 1 < i; i--) {
					if (is_member(stack[i].token, tokens)) {
						var ss = stack.slice(0, i);
						switch (type) {
							case "m":
								return ss.concat(stack[i]).concat(stack[len]);
							case "r":
								return ss.concat(stack[len]);
							case "i":
								return ss;
							case "g":
								return ss.concat(fakeToken("group"));
							case "E":
								return ss.concat(stack[i]);
							case "e":
								return ss.concat(stack[i]);
						}
					}
				}
			}
			return (type == "E" ? [] : stack);
		}

		/////////////////////////////////////////////////////////////////////////////
		// indenter

		function indenter(state, textAfter) {
			var t;
			var unit = cmCfg.indentUnit;
			var wordAfter = wordafter(textAfter);
			var currT = peekToken(state, 1);
			var prevT = peekToken(state, 2);

			if (state.in_string || state.in_atom) {
				return CodeMirror.Pass;
			} else if (!prevT) {
				return 0;
			} else if (currT.token == "when") {
				return currT.column + unit;
			} else if (wordAfter === "when" && prevT.type === "function") {
				return prevT.indent + unit;
			} else if (wordAfter === "(" && currT.token === "fun") {
				return currT.column + 3;
			} else if (wordAfter === "catch" && (t = getToken(state, ["try"]))) {
				return t.column;
			} else if (is_member(wordAfter, ["end", "after", "of"])) {
				t = getToken(state, ["begin", "case", "fun", "if", "receive", "try"]);
				return t ? t.column : CodeMirror.Pass;
			} else if (is_member(wordAfter, closeParenWords)) {
				t = getToken(state, openParenWords);
				return t ? t.column : CodeMirror.Pass;
			} else if (is_member(currT.token, [",", "|", "||"]) ||
								 is_member(wordAfter, [",", "|", "||"])) {
				t = postcommaToken(state);
				return t ? t.column + t.token.length : unit;
			} else if (currT.token == "->") {
				if (is_member(prevT.token, ["receive", "case", "if", "try"])) {
					return prevT.column + unit + unit;
				} else {
					return prevT.column + unit;
				}
			} else if (is_member(currT.token, openParenWords)) {
				return currT.column + currT.token.length;
			} else {
				t = defaultToken(state);
				return truthy(t) ? t.column + unit : 0;
			}
		}

		function wordafter(str) {
			var m = str.match(/,|[a-z]+|\}|\]|\)|>>|\|+|\(/);

			return truthy(m) && (m.index === 0) ? m[0] : "";
		}

		function postcommaToken(state) {
			var objs = state.tokenStack.slice(0, -1);
			var i = getTokenIndex(objs, "type", ["open_paren"]);

			return truthy(objs[i]) ? objs[i] : false;
		}

		function defaultToken(state) {
			var objs = state.tokenStack;
			var stop = getTokenIndex(objs, "type", ["open_paren", "separator", "keyword"]);
			var oper = getTokenIndex(objs, "type", ["operator"]);

			if (truthy(stop) && truthy(oper) && stop < oper) {
				return objs[stop + 1];
			} else if (truthy(stop)) {
				return objs[stop];
			} else {
				return false;
			}
		}

		function getToken(state, tokens) {
			var objs = state.tokenStack;
			var i = getTokenIndex(objs, "token", tokens);

			return truthy(objs[i]) ? objs[i] : false;
		}

		function getTokenIndex(objs, propname, propvals) {

			for (var i = objs.length - 1; - 1 < i; i--) {
				if (is_member(objs[i][propname], propvals)) {
					return i;
				}
			}
			return false;
		}

		function truthy(x) {
			return (x !== false) && (x != null);
		}

		/////////////////////////////////////////////////////////////////////////////
		// this object defines the mode

		return {
			startState: function() {
				return {
					tokenStack: [],
					in_string: false,
					in_atom: false
				};
			},

			token: function(stream, state) {
				return tokenizer(stream, state);
			},

			indent: function(state, textAfter) {
				return indenter(state, textAfter);
			},

			lineComment: "%"
		};
	});

}

manifest.CMoverlays:
function () {

	// Extends CM with includes {{.x.y}} syntax highlighter
	// and supplementary highlighting for color picker

	var re_clr = /^(rgb[a]?\s*\([\s0-9,\.]+\)|#[0-9a-fA-F]{6}|#[0-9a-fA-F]{3})/

	function _parseStream (stream, state) {
		var ch, n1, n2;

		if (stream.match(/^\{\{[\/\.]/)) {
			while ((ch = stream.next()) != null) {
				if (ch == "}" && stream.next() == "}") {
					return "include";
				}
				if (!/[a-z0-9_\/\.\-]/i.test(ch)) break;
			}
		} 
		else if (n1 = stream.match(/^[\[\{"]\/\*{{[\.\/]/)) {
			n2 = n1[0].to(1);
			while (ch = stream.next()) {
				if (
					ch == "}"  
					&& stream.next() == '}'
					&& stream.next() == '*'
					&& stream.next() == '/'
					&& stream.next() == (n2=='{'?'}':n2=='['?']':n2=='"'?'"':'')
				) {
					return "include";
				}
				else if (!/[a-z0-9_\/\.\-]/i.test(ch)) break;
			}
		} else if (stream.match(re_clr)){
			stream.eat(re_clr);
			return "csscolor";
		}
		while (
			stream.next() != null 
			&& !stream.match(/^\{\{[\/\.]/, false) 
			&& !stream.match(/^[\[\{"]\/\*{{[\/\.]/, false)
			&& !stream.match(re_clr, false)
		) {}
		return null;
	}

	// - - - - - - - - - - - - -

	[
		["ddtext", "text"],
		["ddjs", "javascript"],
		["ddhtml", "htmlmixed"],
		["ddcss", "text/css"],
		["ddless", "text/x-less"],
		["ddmd", "gfm"]
	].forEach(function(e){
		if (
			//true || 
			!CodeMirror.modes[e[0]]
		) CodeMirror.defineMode(e[0], function(config, parserConfig) {
			return CodeMirror.overlayMode(CodeMirror.getMode(config,e[1]), {token: _parseStream});
		});
	})

}

manifest.CloseTabs:
function (mask0) {
	// removes tabs that matches mask,
	// does not redraw UI

	var that = this,
			mask = Object.isFunction(mask0)?mask0:function(){return false},
			d = that.data.doc,
			l1 = that.List.length,
			l2 = that.Tablist.length;

	that.List.remove(mask);
	that.Tablist.remove(mask);

	if (that.List.length != l1 || that.Tablist.length != l2) {

		if (that.Tablist[0] && that.List.indexOf(that.Tablist[0])==-1) that.List.push(that.Tablist[0]);
		if (that.List.length) that.Active = that.List[0].id;
		else {
			that.Active = d.items[0].id;
			that.List.push(d.items[0]);
			that.Tablist.push(d.items[0]);
		}
	}
}

inliner.html:
function (data,item,oitems) {
	// data is ext json of type=="inliner",
	// item is dest point, 
	// oitems is {itemId:{item}, itemId1:{item}}

	// Can modify oitems obj –
	// add or remove items of type:"attachment".
	// attachment obj itself must be put into item‘s .data.
	// For remove appropriate key must be set to null.

	// Empty item of type:"attachments" –
	// this.Types.attachment.manifest.data
	// id generator – this.GenId()

	// this points to core app manifest.

	//console.log("Inliner2Html converter running, build 30");

	var that = this,
			att0 = {
				id:"", folder:"", name:"", mime:"", parser:"", parserActive:false,
				ext:"", cache:"864000", data:"", snippet:false, size:"?",
				type:"attachment",
				desc:"", 
				state:{
					savedSdbm:"", sdbm:"Z",
					depends:{}, impacts:{}
				}
			},
			attKeys = Object.keys(oitems).filter(function(e){
				return /inliner\//.test(e);
			}), html = "",
			F = {
				left1000:"max",
				left750:"wide",
				left500:"medium",
				left300:"small",
				left200:"tiny",
				right300:"small-right",
				right200:"tiny-right"
			},
			docid = data._id;

	// mark unexisting appropriate items keys to null
	attKeys.forEach(function(id){
		if (data._id == id.split('/').last(2)[0] && !cw.lib.getref(data, "content._stamp="+id.split('/').last())) {
			oitems[id] = null;
		}
	});

	// get meta
	var metakeys,
			meta = '<meta inliner data-id="' + data._id + 
			'" data-name="'+(data.title||data.name||"").escapeHTML()+
			'" data-tags="'+(JSON.stringify(data.tags)||"").escapeHTML() + '"';

	metakeys = data.meta && Object.isObject(data.meta)?Object.keys(data.meta):[];
	metakeys.forEach(function(key){
		if (/^[a-z0-9]+$/i.test(key) 
				&& !/^id$|^name$|^tags$/.test(key)) meta += ' data-' + key + '="' + data.meta[key].escapeHTML() + '"';
	});

	meta += '/>';

	// get html
	var resrc = {};
	data.content.forEach(function(elt){

		var css = Object.isArray(elt.css)?elt.css:[];

		// add html
		if (elt.type == "html") {
			if (css.length) {
				html+='<section class="'+css.map(function(e){
					return "in-"+e.toLowerCase().replace(/[^a-z0-9]+/ig,'-');
				}).join(" ")+'">'
			}
			html += elt.data;
			if (css.length) html+='</section>'
			html+= '\n\n';
			return;
		}

		// elt.format should be array
		// gen classes for sections
		var classes = '';
		if (Object.isString(elt.format)) elt.format = [elt.format];
		if (Object.isArray(elt.format)) {
			classes = elt.format.reduce(function(res,e){ 
				return res + ' in-item-' + (F[e]?F[e]:e);
			},'')
		}
		if (css.length) {
			classes +=' '+css.map(function(e){return "in-"+e.toLowerCase().replace(/[^a-z0-9]+/ig,'-');}).join(" ");
		} 

		// add embed
		if (elt.type == "embed") {
			html += '<figure class="' + classes + '">' +
				'<section>' + elt.data + '</section>' +
				(elt.showdesc+"" == "show" ? ('<figcaption class="in-item-desc">' + elt.desc + '</figcaption>') : '') +
				'</figure>' + '\n\n';
		}

		// add img

		if (elt.type == "img") {

			var obj, id, file, ref;

			// Create item of type:"attachment"
			if (elt.ref) {
				// image is attached
				ref = elt.ref.replace("inliner/"+docid+"/",'');
				id = cw.lib.hash8("inliner/" + docid +"/"+ref);

				if (data._attachments && data._attachments[elt.ref]) {
					file = data._attachments[elt.ref];
					obj = $.extend(true, {}, att0, {
						id: id,
						folder: "/inliner",
						name: docid+"/"+ref,
						mime: file.content_type,
						data: {
							content_type: file.content_type,
							data: file.data
						},
						state:{
							crc:cw.lib.sdbm(file.data)
						}
					});
					elt.ref = "inliner/"+ obj.name;
				} 
				else {
					obj = $.extend(true, {}, att0, {
						id: id,
						folder: "/inliner",
						name: docid+"/"+ref,
						state:{ crc:false }
					});
				}

			} else {
				// image is inlined
				id = cw.lib.hash8("inliner/" + docid +"/"+elt._stamp);
				obj = $.extend(true, {}, att0, {
					id: id,
					folder: "/inliner",
					name: docid+"/"+elt._stamp+".jpg",
					mime: "image/jpeg",
					data: {
						content_type: "image/jpeg",
						data: elt.data
					},
					state:{
						crc:cw.lib.sdbm(elt.data)
					}
				});

				elt.data = " ";
				elt.ref = "inliner/"+ obj.name;
			}

			if (obj) {
				// put new type:"attachment" item into main doc
				resrc[obj.name] = obj.id;
				if (
					 obj.state.crc && (
						!oitems[id] || 
						oitems[id].state.crc != obj.state.crc
					 )
				) oitems[id] = obj;
			}

			if (obj) html += '<figure class="' + classes + '">' +
				'<img src="' + obj.folder.slice(1) + '/' + obj.name + '"/>' +
				(elt.showdesc+"" == "show" ? ('<figcaption class="in-item-desc">' + elt.desc + '</figcaption>') : '') +
				'</figure>' + '\n\n';
		}

		// add widget-code
		if (elt.type == "code" && elt.tab && elt.tab.length) {
			html += '<div id="inliner-widget-code-'+elt._stamp+'" class="inliner-widget-code">';
			elt.tab.forEach(function(type){
				html += '<div id="x'+type+'" class="dib vat oh w750">' +
					'<textarea id="' + type + '" class="w750 fs80"></textarea>' +
					'</div>';
			});

			html += '</div>';

			html += '<script>' + 
				'$(function(){' +
				// TODO cut useless elt fields
				'var elt=' + cw.lib.js2txt(elt) + ';\n' +
				'initCode(elt);\n' +
				cw.lib.js2txt(initCode) +
				'})' +
				'</scr' + 'ipt>';
		}

		// add widget
		if (elt.type == "widget") {
			html += '<div id="inliner-widget-'+elt._stamp+'" class="inliner-widget"></div>';

			html += '<scr' + 'ipt>' + 
				'$(function(){' +
				'var elt=' + cw.lib.js2txt(elt) + ';\n' +
				'initWidget(elt);\n' +
				cw.lib.js2txt(initWidget) +
				'})' +
				'</scr' + 'ipt>';
		}
	});

	Object.keys(oitems).forEach(function(i){
		var it = oitems[i];
		if (it && it.folder=="/inliner" && it.name.startsWith(docid+"/")) {
			if (!resrc[it.name] || resrc[it.name] != it.id) oitems[i] = null;
		}
	});

	//item.data = JSON.stringify(data, null, '\t');
	item.data = meta + '\n' + html;

	return undefined;


