						type: "widget",
						tab:["manifest"],
						js:"// Widget manifest\n({\n\tinit: function ($form, form) {\n\t\t// Init code\n\t},\n\tui: "
						+"{\n\t\t// UI bindings\n\t}\t,\n\tstyle: {\n\t\t// Local CSS rules\n\t}\n})",
						html:"",
						data:"({\n\t//Put default data here\n})",
						state:"",
						valid:false,
						err:[],
						options:[]
					},
					init: function ($o, form) {
						var that = this;
						$o.formgen(this.HTML);
						$o.then(function(){
							if (form.data.state) {
								$o.find("#btn-run").trigger("click");
							} else that.XCode($o, form.data);
						});
					},
					ui:{
						"#tab":{
							bind:function (d,v,$o){
								if (null!=v) {
									var $x = $o.my("find","#xcode");
									if ($x.is(".my-form")) $x.my("redraw");
									d.tab = v;
								}
								return d.tab;
							},
							init: function ($o) {
								$o.tags({tags:[{"HTML":"html"}, {"Manifest":"manifest"}, {"Defaults":"data"}]})
							}
						},
						"#btn-run":{
							bind: function (d,v,$o){
								if (v!=null) {
									if (d.valid) {
										d.state = "running";
										$o.my("find","#xcode,#tab").addClass("hide");
										var $a = $o.my("find","#xapp"),
												$v = $o.my("find","#xreflect");
										$a.removeClass("hide")
										.html(d.html)
										.my(eval(d.js),eval(d.data)||{})
										.then(function(){
											$a.trigger("change");
										}.debounce(50));

										if (d.options.indexOf("show data")!=-1) {
											$a.on("change.my", function(){
												$v.html(
													cw.lib.js2txt(
														$a.my("data"),"  ")
													.escapeHTML()
													.replace(/^\{/,'{ <span class="green">// Form data</span>')	
												);
											}.debounce(2));
											$a.addClass("xdata");
											$v.addClass("xdata");
										} else {
											$a.removeClass("xdata");
											$v.removeClass("xdata");
										}
									} else {
										cw.lib.note(d.err.join(".<br>")+".", "error", 5000);
									}
								}
							},
							css:{
								hide: function(d){return !!d.state},
								"red": function (d) {return !d.valid},
								"green": function (d) {return d.valid}
							},
							events:"click.my",
							watch:"#btn-code"
						},
						"#btn-code":{
							bind: function(d,v, $o){
								if (v!=null) {
									d.state = "";
									$o.my("find","#xapp").addClass("hide").my("remove");
									$o.my("find","#xcode, #tab").removeClass("hide");
									$o.my("find","#xreflect").removeClass("xdata");
									var $x = $o.my("find","#xcode");
									if (!$x.is(".my-form")) {
										this.XCode($o.my().root, d);
									}
								}
							},
							css:{
								hide: function(d){return !d.state}
							},
							events:"click.my",
							watch:"#btn-run"
						},
						"#warn":{
							bind:function (d) {
								var t=d.js, o, err=[], data;
								try { o = eval(t); } catch (e) {}
								try { data = eval(d.data); } catch (e) {}
								d.valid=false;
								if (!Object.isObject(o)) err.push("Invalid manifest");
								else {
									if ( !Object.isObject(o.ui)) err.push("No ui section");
									else if ( !Object.size(o.ui)) err.push("No bindings in ui section");

									if  ( !Object.isFunction(o.init) && !Object.isArray(o.init) && !(d.html+"").length)
										err.push("No HTML defined in init function or HTML tab");

									if (!Object.isObject(data))  err.push("Defaults must be an object");

									if (o.data!==undefined && !Object.isObject(o.data)) err.push("Section ‘data’ must be an object");
									if (o.params!==undefined && !Object.isObject(o.params)) err.push("Section ‘params’ must be an object");
									if (o.style!==undefined && !Object.isObject(o.style)) err.push("Section ‘style’ must be an object");
								}
								if (!err.length) d.valid = true;
								d.err=err;
							},
							delay:500,
							recalc:"#btn-run"
						}
					},
					XCode: function ($form, d){
						this.XCode3._compiled = this._compiled;
						$form.find("#xcode")
						.my (this.XCode3, d)
						.on("change", function(){
							$form.find("#warn").trigger("check.my");
						});
					},
					XCode3:{
						ui:{
							"#xjs":{css:{hide: function (d) { return d.tab.indexOf("manifest")==-1;}}},
							"#js":{
								bind:"js",
								init: function ($o, form){ 
									if (!this._compiled || form.data.tab.indexOf("manifest")>-1) this.Cm($o, "javascript", true);
								}
							},

							"#xdata":{
								css:{
									hide: function (d) { return d.tab.indexOf("data")==-1;},
									bt1: function (d)  { return d.tab.length>1;}
								}
							},
							"#data":{
								bind:"data",
								init: function ($o, form){ 
									if (!this._compiled || form.data.tab.indexOf("data")>-1) this.Cm($o, "javascript", true);
								}
							},

							"#xhtml":{
								css:{
									hide: function (d) { return d.tab.indexOf("html")==-1;},
									bb1: function (d)  { return d.tab.length>1;}
								}
							},
							"#html":{
								bind:"html",
								init: function ($o, form){ 
									if (!this._compiled || form.data.tab.indexOf("html")>-1) 
										this.Cm($o, "htmlmixed", true, "base16-light");
								}
							},

							"input[name=options]":"options"
						},
						Cm: function ($o, mode, lineNums, theme) {
							CodeMirror.fromTextArea($o[0], {
								mode: mode,
								lineNumbers:lineNums,
								indentWithTabs:!0,
								indentUnit:2,
								tabSize:2,
								theme: theme||"default"
							});
						}
					},
					Compiler: function ($form, html, css, repo, manifest) {
						var man = Object.clone(Object.reject(manifest, ["Compiler","Hoverpane", /^_/]), true),
								data = $form.my("data"),
								tid = "form"+Number.random(1e6,1e7),
								h="", height,
								insync=false;
						man.style[" #xoptions"] = "display:none;";
						man.style[" #tab"] = "display:none;";
						man._compiled = true;

						height = $form.height() - (!data.state?18:-7);
						insync = data.options.indexOf("init sync")>-1;

						if (data.options.indexOf("noedit")>-1) {
							// Include only executable manifest
							if (data.err.length) throw (new Error ("Error compiling widget manifest") );
							else {
								this.CSSCompiler($form, css);

								var refl = data.options.indexOf("show data")>-1;

								h+=[
									'<span id="'+tid+'" class="db '+$form.attr("class")+'" '
									+'style="height:'+height+'px;overflow:hidden;">',
									'<div class="cw-widget">',
									'<div id="xapp" class="oh vat'+(refl?' xdata':'')+'">'+data.html+'</div>',
									(refl?'<div id="xreflect" class="oh vat xdata"></div>':""),
									'</div>',
									'</span>',
									'<script>',
									'$'+(insync?'':'.my.chain')
									+'(function(){var $f=$("#'+tid+'");$f.find("#xapp").my('+data.js+", ",
									data.data+')',
									(refl?'.on("change.my", function(){'
									 +'$(this).parent().find("#xreflect").html('
									 +'cw.lib.js2txt($(this).my("data"),"  ").replace(/^\{/,\'{ <span class="green">// App data</span>\')'
									 +');'
									 +'}.debounce(20))':""),
									'.then(function(){$f.css({"height":"none", overflow:"none"});}.debounce(100))',
									'});',
									'<'+'/script>'
								].join("\n");

								html.push(h);
							}

						} else {
							// Include full code
							var norepo = !repo[man.id];
							repo[man.id]=true;
							h+=[
								'<span id="'+tid+'" class="db cw-item" style="height:'
								+height+'px;overflow:hidden;"></span>',
								'<script>',
								(norepo?'$.my.cache('+cw.lib.js2txt(man,"  ")+');' : ''),
								'$'+(insync?'':'.my.chain')
								+'(function(){var $f=$("#'+tid+'"); $f.my("'+man.id+'", ',
								cw.lib.js2txt(data,"  ").replace(/<\//g, '<\\u002F')+')',
								'.then(function(){$f.css({"height":"none", overflow:"none"});}.debounce(100))',
								'});',
								'<'+'/script>'
							].join('\n');
							html.push(h);
						}
					},
					HTML:[
						{row:"750px", label:"0px"},
						'<div class="cw-widget">',
						'<div class="w280 db oh fs90 lh130" id="ovl">',
						'<div class="w200 dib pt7 vat">',
						'<span id="tab" class="fs85"></span>',
						'</div>',
						'<div  class="w80 dib vat fs95">',
						'<span id="btn-run" class="button fs80 lh130 pb6 fi-play-circle green br100 mr0 fr"> ',
						'<span class="fs110">Run&nbsp;</span>',
						'</span>',
						'<span id="btn-code" class="hide button fs80 fi-pencil lh130 green br100 mr0 fr">&thinsp;&thinsp;',
						'<span class="fs110">Code</span>',
						'</span>',
						'</div>',
						'</div>',
						'<div id="xcode" class="w750">',
						'<div id="xhtml" class="dib vat oh w750 ">',
						'<textarea id="html" class="w750 fs80"></textarea>',
						'</div>',
						'<div id="xjs" class="dib vat oh w750 ">',
						'<textarea id="js" class="w750 fs80"></textarea>',
						'</div>',	
						'<div id="xdata" class="dib vat oh w750 ">',
						'<textarea id="data" class="w750 fs80"></textarea>',
						'</div>',
						'<div id="xoptions" class="fs75 bt1 pl7 pt3 pb3">',
						'<label>',
						'<input name="options" type="checkbox" value="noedit" />',
						' &nbsp;Disable editor at runtime',
						'</label>',
						'<label>',
						'<input name="options" type="checkbox" value="show data" />',
						' &nbsp;Reflect form’s data',
						'</label>',
						'<label>',
						'<input name="options" type="checkbox" value="init sync" />',
						' &nbsp;Init sync',
						'</label>',
						'</div>',
						'</div>',
						'<div id="xapp" class="oh hide vat fs80"></div>',
						'<div id="xreflect" class="oh vat"></div>',
						'<div id="warn" class="hide"></div>',
						'</div>'
					],
					style: {
						" .b0": "border:none!important;",
						" .CodeMirror":"font:14px 'PT Mono',Monaco,monospace;line-height:1.4em;height:auto;",
						" #xapp": {
							"":"padding:20px 25px 25px 25px;display:inline-block;",
							".xdata" :"width:445px;padding-right:20px"
						},
						" #ovl" :"transition: opacity 0.3s; opacity:0.6;position:absolute; top:2px;right:5px;z-index:+2;",
						" .bb1":"border-bottom:1px solid #C1D0DA;",
						" .bt1":"border-top:1px solid #C1D0DA;",
						"":{
							"": "border:1px solid #C1D0DA; border-radius:4px;overflow:hidden; display:block; min-height:48px;"
							+"clear:both;margin: 0 0 0.9em 0;padding-bottom:0!important;",
							":hover #ovl":"opacity:1"
						},
						" label":"display:inline-block; color:#777; margin-right:20px;",
						" #xreflect":{
							"":"padding:0px 7px 0px 15px; 10px; font:12px 'PT Mono',Monaco,monospace;line-height:1.2em;height:auto;"
							+"margin:18px 0 18px 0; border-left:1px dotted #C1D0DA; opacity:0.7;"
							+"white-space: pre-wrap;word-wrap: break-word;display:none;",
							".xdata": "display:inline-block;width:297px;"
						},
						" .my-form-list .fi-list":"opacity:0.8;cursor:move"
					}	
				};	

		//update man
		$.extend(true, man, {
			init: function ($o, form) {
				var that = this;
				$o.formgen(this.HTML);
				$o.then(function(){
					if (form.data.state || form.data.options.find('noedit')) {
						$o.find("#btn-run").trigger("click");
						if (form.data.options.find('noedit')) {
							$o.find("#btn-run, #btn-code").css({'display':'none;'});
						}
