manifest.Att:
function (fname,rtype) {
	//return undefined;

	// Returns attach in appropriate format, defined by rtype,
	// "url" returns session or data URL,
	// "data" returns data URL.
	// If rtype is object {content_type:"mime/type", data:"b64"},
	// appends this object to doc attachments.

	var that = this,
			u = that.SessionUrls || (that.SessionUrls = {}),
			d = that.data.doc,
			a = d._attachments || (d._attachments = {}),
			pending = that.PendingUrls || (that.PendingUrls = {});

	// Store
	if (fname && Object.isObject(rtype)) {
		a[fname] = rtype;
		that.my.find("#atts").trigger("recalc");
		return a;
	}

	// Recalc atts
	if (fname === true) {
		that.my.find("#atts").trigger("recalc");
	}

	if (fname == null) return a;

	if (rtype == "session") return u;

	if (rtype=="url" && u[fname]) return u[fname];
	else {
		var pi = $.Deferred(),
				id = that.data.doc._id,
				db = that.db;

		if (!id) pi.reject();
		else {

			//-------------------

			if (rtype=="url") {

				if (a && a[fname] && a[fname].data) {
					return 'data:'+a[fname].content_type+';base64,'+a[fname].data;
				}

				if (pending[fname]) {
					pi.resolve();
					return pending[fname];
				}

				else {
					pending[fname] = pi.promise();
					cw.db(that.db.name)
					.getAttachment(id, fname)
					.then (
						function(blob){
							var url = URL.createObjectURL(blob);
							u[fname] = url;
							pi.resolve(url);
							pending[fname] = null;
						},
						function(){ 
							pi.reject(); 
							pending[fname] = null;
						}
					);
				}
			}

			//-------------------

			else if (rtype=="data") {

				if (a && a[fname] && a[fname].data) {
					pi.resolve (a[fname].data);
				}
				else {
					cw.db(that.db.name).getAttachment(id, fname)
					.then (
						function(blob){
							$.my.f.blob2base64(blob, function(data){ pi.resolve(data); });
						},
						function(){ pi.reject(); }
					);
				}
			}

			//-------------------

			else if (rtype == "preview") {
				var names = [],
						res = [];
				if (Object.isString(fname)) names.push(fname);
				else if (Object.isArray(fname)) names = fname;
				else if (Object.isRegExp(fname)) {
					names = Object.keys(a).filter(fname).sortBy();
				}
				if (!names.length) pi.resolve([]);
				else {
					var icos = d.previews || (d.previews = {}),
							subnames = Object.keys(icos).intersect(names);
					if (subnames.length == names.length) {
						// we have all previews
						names.forEach(function(i){
							res.push($.extend({fname:i}, icos[i]));
						});
						pi.resolve(res);
					} else {
						// we have to generate previews
						subnames.forEach(function(i){
							res.push($.extend({fname:i}, icos[i]));
						});
						var missing = names.subtract(subnames),
								pis = [];

						missing.forEach(function(i){
							var pi0 = $.Deferred();
							pis.push(pi0);
							if (a[i] && a[i].data) {
								_genPreview(i, a[i].content_type, a[i].data, pi0);
							}
							else {
								// load attach
								cw.db(that.db.name).getAttachment(id, i)
								.then (
									function(blob){
										$.my.f.blob2base64(blob, function(data){ 
											_genPreview(i, a[i].content_type, data, pi0);
										});
									},
									function(){ pi0.resolve(null); }
								);
							}
						}); // end forEach

						// now pis is list of promises
						$.when.apply(null, pis)
						.done(function(){
							for (var t, j=0; j<arguments.length;j++) {
								var t = arguments[j];
								if (t != null) icos[t.fname] = t;
							}
							missing.forEach(function(i){
								if (icos[i]) res.push(icos[i]);
							});
							res.sortBy('fname');
							pi.resolve(res);
						});

					}

				}
			}
		}

		return pi.promise();

		// - - - - - - - - - - - - - - - 

		function _genPreview(name,mime, data, pi0){
			var $i = $('<img src=""/>'),
					pv = {
						fname:name,
						size:'?',
						desc:'',
						dim:'?×?',
						data:''
					};
			$i.attr("src",'data:'+mime+';base64,'+data);
			$i.load(function(){
				var ico, 
						img = cw.lib.image($i,"white");
				pv.dim = img.width() + '×'+ img.height();
				var l = (data.length/4*3).round();
				pv.size = l.metric(l>1e6?2:0,false).replace(/([a-z])$/i,' $1')+"b";
				ico = img.resample(150);
				if (ico.height()>150) ico.resample(22500/ico.height());
				pv.data = ico.sharpen(0.2).contrast(1.05).jpeg(0.65);
				$i.remove();
				pi0.resolve(pv);
			});
		} 
	}

}

