({
	name:"HTML",
	ext:"html",
	mime:"text/html",
	primary:true,
	stringify: function(e) { return e.data+"" },
	postprocess: function(item, att){
		var that = this;
		if (!item.Uglify) return att;
		else {
			// compress and uglify 
			var s = _compress(att+"");
			return (item.desc?"<"+"!-- "+item.desc+"--"+">\n":"") + s;
		}

		//- - - - - - - - - - -

		function _compress (h){

			var parse = that.HTMLParser,
					keepSpace = [],
					stack=[],
					res=[];

			parse(h, {
				start: function(tag, attrs, unary) {
					var t = tag.toLowerCase(),
							html = _construct(t, attrs, unary);
					res.push(html);
					if (!unary) {
						if (/^(script|pre|textarea)$/.test(t)) {
							keepSpace.push(true);
						}
						stack.push({tag:t, attrs:attrs});
					}
				},
				end: function(tag) {
					var a, s, process, type, src,
							t = tag.toLowerCase();
					if (t=="script") {
						a = stack.last().attrs;
						process = true;
						if (a && a.length) {
							type = a.find(function(e){return e.name=="type"});
							src = a.find(function(e){return e.name=="src"});
							if (type && !/javascript$/i.test(type.value) && !type.value=="js") process=false;
							if (src) process = false;
							if (type && type.value == "text/x-jquery-tmpl") res.push(_compact(res.pop()));
						}
						if (process) {
							s = res.pop();
							try {s = _compressJS(s);} catch(e){}
							res.push(s);
						}
						keepSpace.pop();
					} 
					else if (t=="pre" || t=="textarea") keepSpace.pop();

					res.push('</'+t+'>');
					stack.pop(); 
				},
				chars: function(chars) {
					if (keepSpace.length) res.push (chars);
					else res.push (_compact(chars));
				},
				doctype: function(t) {res.push(t);},
			});

			return res.join("");
		}


		//- - - - - - - - - - -

		function _compact(s){
			return s.replace(/[\s\n\r\t]+/g,' ')
		}


		//- - - - - - - - - - -

		function _compressJS (s0) {
			var s = s0;
			// compress and uglify 
			var u = UglifyJS,
					ast = u.parse(s+"");
			ast.figure_out_scope();
			ast = ast.transform(u.Compressor({
				side_effects: false
				//inline_script:true
			}));
			ast.figure_out_scope();
			ast.mangle_names();
			return ast.print_to_string().replace(/<\/script>/ig,'<\\u002fscript>');
		}


		// - - - - - - - - - - - - - - - - - 

		function _construct (tag, attrs, unary) {
			var i, c={id:null}, h = '<'+tag;
			if (attrs && attrs.length) {
				for (i=0;i<attrs.length;i++) {
					c[attrs[i].name] = attrs[i].name.toLowerCase()+(attrs[i].value!=null?'="'+attrs[i].escaped+'"':'');
				}
				Object.keys(c).forEach(function(e){ if (null!=c[e]) h+=" "+c[e]; });
			}
			h+=unary?'/>':'>';
			return h;
		}
	},
	defaults:{ 
		data:'<!-- HTML -->\n\n\n',
		Uglify: false
	},
	extend:{
		CMmode: function(d){
			var s = {readOnly:!!d.external && !/^[x\s]/.test(d.external)};
			if (d.noIncludes) s.mode = "htmlmixed";
			else s.mode = "ddhtml";
			if (s.readOnly) s.mode = "text";
			return s;
		},
		Settings:{
			ui:{
				'input[name="uglify"]':{
					bind: function(d,v){
						if (v!=null) d.Uglify = !!(v+"");
						return d.Uglify?["uglify"]:[];
					}
				},
				"#desc":{
					bind:"desc",
					css:{
						hide: function(d){ return !d.Uglify }
					},
					watch:'input[name="uglify"]'
				}
			},
			Aux:[
				[" ", 
				 '<label class="gray fs80 dib">',
				 '<input type="checkbox" name="uglify" value="uglify"/>',
				 '<span> Compress HTML code (experimental)</span>',
				 '</label>'
				],
				{rowCss:"my-row pt3 pb10"},
				["Comment", 'inp#desc', {plc:"Inserted before compressed code"} ]
			]
		}
	},
	replace:{
		//HTML:[]
	},
	manifest:null
})

({
	name:"JS",
	ext:"js",
	mime:"text/javascript",
	stringify: function(item) {  	return item.data+""; },
	/*s2o:function(src) {
		var o={};
		o = eval("("+src+")");//JSON.parse(s);
		return o;
	},*/
	s2o:function(src) {
		var o, err1, err2, ast;
		// First, try eval assuming we have obj or function 
		// inside our source
		try {
			o = eval("("+src+")");//JSON.parse(s);
		} 
		catch (e) { err1 = e; }

		if (err1) {
			// if eval fail, try Uglify, and if it parses – return source
			try {
				ast = UglifyJS.parse(src);
			} 
			catch (e) { err2 = e; }
			if (!err2) return src; 
			throw err2;
		}

		if (err1) throw err1;

		return o;
	},
	postprocess: function(item, att){
		if (!item.Uglify) return att;
		else {
			// compress and uglify 
			var u = UglifyJS,
					ast = u.parse(att+"");
			ast.figure_out_scope();
			ast = ast.transform(u.Compressor({side_effects: false}));
			ast.figure_out_scope();
			// No char freq opt to avoid 
			// different results from the same code
			//ast.compute_char_frequency();
			ast.mangle_names();
			return (item.desc?"/*"+item.desc.replace(/(\/\*|\*\/+)/g,'')+"*/\n":"") 
			+ ast.print_to_string().replace(/<\/script>/ig,'<\\u002fscript>');
		}
	},
	defaults:{
		data:'/* JS */\n\n\n',
		Uglify: false
	},
	extend:{	
		CMmode: function(d){
			var s = {autoMatchParens: true, readOnly:!!d.external && !/^[x\s]/.test(d.external)};
			if (d.noIncludes) s.mode = "javascript";
			else s.mode = "ddjs";
