			return parse.errors.join("<br>");
		},
		Settings:{
			ui:{
				'input[name="uglify"]':{
					bind: function(d,v){
						if (v!=null) d.Uglify = !!(v+"");
						return d.Uglify?["uglify"]:[];
					}
				},
				"#desc":{
					bind:"desc",
					css:{
						hide: function(d){ return !d.Uglify }
					},
					watch:'input[name="uglify"]'
				}
			},
			Aux:[
				[" ", 
				 '<label class="gray fs80 dib">',
				 '<input type="checkbox" name="uglify" value="uglify"/>',
				 '<span> Uglify code</span>',
				 '</label>'
				],
				{rowCss:"my-row pt3 pb10"},
				["Comment", 'inp#desc', {plc:"Inserted before uglified code"} ]
			]
		},
		validator: function(d){
			var r, f, s = d.data;
			try {
				f = new Function([], (s.to(1)=="{" && s.from(-1)=="}"?"return("+s+");":s));
