					bind: function(d,v) {
						if (v!=null) {
							d.name=d.ext=d.folder="";
							var sep = v.to(1);
							var a = v.split(sep);
							if (a.length==2) {
								d.folder=sep;
								d.name = a[1];
							} else {
								d.folder=sep+a[1];
								d.name = a.from(2).compact().join(sep);
							}

							return v;
						}
						var s =  d.folder+(d.folder.length>1?d.folder.to(1):"")+d.name;
						return s;
					},
					check: function(d,v){
						if (!v) return "Enter path like <b>/folder/fname.ext</b> for attach";
						if (!/^[\/]/.test(v)) return 'Attachment path must start with /';
						if (v.length<2) return "Path can not be empty";
						if (v.indexOf(" ") !=-1) return 'Path can’t contain spaces';
						if (v.to(1)==".") {
							if (v.last()==".") return "Branch path can not end with .";
						}
						if (this.RT().getDup(d,v)) return "Resource already exist"
							}
