({
	name:"less",
	ext:"less",
	mime:"text/x-less",
	stringify: function(e) { return e.data+""},
	defaults:{ data:'/* LESS */' },
	postprocess: function(item, att){
		if (!item.Uglify) return att;
		else {

			var ret="";
			less.render(att+"", function(err, res){
				if (err) {
					throw {message:err.message+", line "+err.line};
				}
				else ret = res.css;
			})
			return (item.desc?"/* "+item.desc.replace(/(\/\*|\*\/+)/g,'')+" */\n":"")+ret;
		}
	},
	extend:{
		CMmode: function(d){
			var s = {readOnly:!!d.external && !/^[x\s]/.test(d.external)};
