(function($o, form){
	var that = this;
	$o.html($.my.formgen(this.HTML)+$.my.formgen(this.HTMLtail));
	$o.then(function(){
		$o.find("#xparser").addClass("hide").css({opacity:1});
		$o.on("change.my", function(){
			that.Track(form.data);
			if (form.data.state.sdbm !== form.data.state.savedSdbm) {
				$o.parent().my("find","#ilist").trigger("check");
			}
		}.debounce(300));
	});

	// Error details modal

	$o.on("click.my","#warn", function(){
		var $c = $(this);
		if ($c.data("modal")) return $c.modal(true);
		if (that.Err) {
			var e = that.Err(that.data.data);
			$c.modal({
				width:250,
				nose:"top",
				bound:0,
				align:"top:120%;left:0;",
				esc:true,
				enter:true,
				manifest:{
					init:['<span id="t" class="red lh150"></span>'],
					ui:{"#t":"t"}
				},
				data:{t:e},
				css:"fs80"
			});
		}
	});


	// Color Picker magic

	$o.on("contextmenu.my",".cm-csscolor", function(evt){
		var $i = $(evt.currentTarget),//$(this),
				c = $i[0].innerText+"",
				cm = $i.parents("#xeditor").find("#editor").next()[0].CodeMirror,
				x = evt.pageX,
				y = evt.pageY,
				up = $(window).height()/2>y;

		$i.modal({
			width:272,
			css:"p0",
			manifest:that.RT().ColorPicker,
			data:{css:c},
			align:up?"top:100%":"bottom:140%",
			nose:up?"top":"bottom",
			screen:"rgba(255,255,255,0.5)",
			esc:true,
			enter:true,
			close:false,
			focus:"#cprgb"
		})
		.then(function(picker){
			//console.log(res);
			var co = cm.coordsChar({left:x, top:y}),
					line = co.line,
					idx = co.ch,
					cd = cm.getDoc(),
					s = cd.getLine(line),
					sl = s.length,
					res = s;
			for (var i=idx; i>-1; i--) {
				var s0 = s.substr(i);
				if (s0.startsWith(c)) {
					res = s.to(i) + s0.replace(c, picker.css.toLowerCase());
					break;
				}
			}
			cd.replaceRange(res, {line:line, ch:0}, {line:line, ch:sl})
			//$i.text(picker.css).trigger("input");
		});
		return false;
	});

	if (that.params && that.params.restyle) {
		$o.then(function(){
			$o.my("restyle");
		}.debounce(150))
	}
})

