			var ed = CodeMirror.fromTextArea($o[0], {
				mode: "text",
				lineNumbers:true,
				indentWithTabs:!0,
				indentUnit:2,
				tabSize:2,
				theme: "default"
			});
			Object.keys(m).forEach(function(i){ ed.setOption(i,m[i]); });

			var $cm = $o.next()
			if (m.readOnly) $cm.addClass("readonly");
			else $cm.removeClass("readonly");
		},
		bind:"data"
	},

	"#warn":{
		bind: function(d, v, $o){
			var r, that = this;
			if (Object.isFunction(that.validator)){
				try {
					r = that.validator(d);
				} catch (e) {
					r = e.message;
				}
			}
			$o.html(r?'&nbsp;<span class="'+(that.Err?'pseudolink red0 o90':'')+'">'+r+'</span>':"");
			$o.toggleClass("hide", !r);
			$o.my("find","#xeditor").toggleClass("b1r", !!r);
		},
		watch:"#editor",
		delay:200
	},

	"#found":{
		delay:200,
		bind: function(d){
			var that = this,
					s = that.RT().Search,
					lines = [];
			if (/^.{2,}$/.test(s) && that.GetActiveId() == d.id) {
				var re = new RegExp (s.split(" ").compact(true).map(RegExp.escape).join("|"), "i");
				d.data.lines().forEach(function(l, i){
					if (re.test(l)) lines.push(i+1);
				});
				if (lines.length) return '<span class="fi-magnifying-glass fs90 o70"></span> Found @'+lines.join(", ");
				else return "";
			} else return "";
		}
	},

	"#fname":{
		bind: function(d){
			var html="", 
					base = this.GetDoc().export.path, 
					p = !d.snippet && (d.type!="attachment");

			html+= d.folder+(d.folder.length>1?d.folder.to(1):"");
			html+= d.name

			return html;
		}
	},

	"#btn-settings":{
		bind: function(d,v,$o) {
			var that = this, $f;
			if (v!=null) {
				$f = $o;//.my().root;
				if ($f.data("modal")) $f.modal(true);
				else $f.modal({
					manifest: this.Settings,
					data:Object.select(
						d, 
						(
							"id type desc folder mime tags size name ext cache"
							+ " parserActive noIncludes snippet external"
							+ " stashResolved isBinary"
						).split(" ").add([/^[A-Z]/])
					),
					done: function(err){
						if (err) {
							cw.lib.note("Fix errors","error");
							return true;
						}
					},
					esc:true,
					enter:true,
					align:"top:0;right:0", 
					screen:"rgba(255,255,255,0.5)",
					css:"fs100"
				}).then(function(res){

					// moveAtt
					var nold = that.RT().Path(d).from(1),
							nnew = that.RT().Path(res).from(1),
							doc = that.RT().data.doc;
					if (nold != nnew && doc._attachments) {
						delete doc._attachments[nold];
					}
					_fin();

					function _fin(){
						$.extend(d, res);
						if (that.CMmode) {
							var m = that.CMmode(d),
									$cm = $o.my("find","#editor").next(),
									cm =  $cm[0].CodeMirror;

							Object.keys(m).forEach(function(i){ cm.setOption(i,m[i]); });
							if (m.readOnly) $cm.addClass("readonly");
							else $cm.removeClass("readonly");
						}
						$o.my("find","#fname").trigger("check");
					}
				});
			}
		},
		watch:"#fname"
	},
	"#showhideproxy":{
		bind: function(d,v,$o) {
			$o.my().root.toggleClass("hide", this.GetActiveId() !== d.id);
		}
	}
})

