				}
			}
		},
		"#file": {
			"bind": function (d, v, $o){

	    		var f,that = this,
					$r = $o.my().root,
					$img = $r.find("#source"),
					img="";


				if (v!=null && v) {
					f = $o[0].files[0];
					if (f) (function(){
						d.filename = f.name
							.toLowerCase()
							.replace(/[^a-z0-9\-\.]/gi,'');
							//.replace(/\.[^\.]+$/,"")+".jpg";

						var ext = d.filename.split(".").last();
						if (ext=="png") d.quality="1";
						else {
							d.filename = d.filename.replace(/\.[^\.]+$/,"")+".jpg";
							d.quality="0.98";
						}

						var fr = new FileReader(), ri, sb64 = [];
						fr.onload=function(e){
							ri = new Uint8Array(e.target.result);
							for (var i=0; i<ri.length; i++) sb64.push(String.fromCharCode(ri[i]));
							img = window.btoa(sb64.join(""));
						};

						fr.onloadend = function (){
							$img.load(function(){
								d.scale = $img[0].naturalWidth/$img.width();
								$img.Jcrop({
									onChange: preview,
									onSelect: preview.debounce(20),
									allowMove:true,
									bgColor:"white"
								}, function (){
									this.animateTo([0,0,100,100]);
									d.crop.z=1;
									$o.my("find","#folder").trigger("check");
								});
								img="";
								$o.parent().addClass("hide");
							});
							$img.removeClass("hide")
								.attr("src","data:image/jpeg;base64,"+img);

							//d.scale=$img[0].naturalWidth/$img.width();	
						};
						fr.readAsArrayBuffer(f);	
					})();
				}

				function preview(c){
					$.extend(d.crop, Object.select(c,["x","y","w","h"]));
					d.cropped=true;

					that.Recalc(d, $o, true);
				}
			}
		},
