({

	"bind": function (d,v,$o) {
		var f, that = this,
				$r = $o.my().root,
				$c = $r.find("#x1"),
				$img = $r.find("#source"),
				$c2 = $r.find("#x2"),
				n={k:d.scale}, img="",
				width=d.width*1.5;

		if (v!=null && d.cropped) {
			_create (d.crop);

			if (d.quality+"" !="1") {
				d.data = $o.my("find","#x1")[0].toDataURL('image/jpeg',+d.quality).substr(23);
				d.mime = "image/jpeg";
			} else {
				d.data = $o.my("find","#x1")[0].toDataURL('image/png').substr(22);
				d.mime = "image/png";
			}

			var ico = cw.lib.image($c,"white").resample(120);
			if (ico.height()>120) ico.resample(14400/ico.height())
			d.preview = ico.sharpen(0.1).contrast(1.05).jpeg(0.65);

			$o.trigger("commit");
		}

		function _create (c){
			if(parseInt(c.w) > 0) {
				var img = $img[0], cs, ctx, cs2, ctx2, 
						k = n.k,
						w = c.w*k, // scale width/height
						h = c.h*k; 

				if (!that.Active) {
					cs = $c[0];
					cs.width=d.width;
					cs.height=d.height;
					ctx = cs.getContext("2d");
					ctx.drawImage(img, (c.x*k).floor(), (c.y*k).floor(), d.width*1, d.height*1, 0, 0, d.width*1, d.height*1);

				} else { 
					if (w>d.width*2) {
						cs2 = $c2[0];
						cs2.width=d.width*2;
						cs2.height=2*d.height;
						ctx2 = cs2.getContext("2d");
						ctx2.rect(0, 0, cs2.width, cs2.height);
						ctx2.fillStyle='white';
						ctx2.fill();
						ctx2.drawImage(img, c.x*k, c.y*k, w, h, 0, 0, d.width*2,d.height*2);

						cs = $c[0];
						cs.width=d.width;
						cs.height=d.height;
						ctx = cs.getContext("2d");
						ctx.drawImage(cs2, 0, 0, d.width*2,d.height*2, 0, 0, d.width*1, d.height*1);
					} else {
						cs = $c[0];
						cs.width=d.width;
						cs.height=d.height;
						ctx = cs.getContext("2d");
						ctx.rect(0, 0, cs.width, cs.height);
						ctx.fillStyle='white';
						ctx.fill();
						ctx.drawImage(img, c.x*k, c.y*k, w, h, 0, 0, d.width*1, d.height*1);
					}
				}
			}
		}
	},
	"events": "click.my",
	watch:"#folder",
	css:{
		":disabled": function(d){ return !Object.size(d.crop)}
	}

})

