	if (creds) headers.Authorization = "Basic "+ cw.lib.base64(creds);

	for (i=0;i<doc.items.length;i++) {
		if ( !doc.items[i].snippet){
			j = that.Path(doc.items[i])
			if ( j.to(1) != ".") items[j.from(1)] = doc.items[i];
