			} else {
				path = ref.split(".").each(function (a,i){this[i]=String(a).compact();});
				ptr = data;
				for (;i<path.length;i++) {
					if (i===path.length-1) ptr[path[i]] = val; //we'r in the hole
					else {
						if (i===0) {
							ptr = data[path[0]];
							preptr= data;
						}
						else {
							preptr = preptr[path[i-1]];
							ptr = ptr[path[i]];
