	function _load (doc0) {
		console.log("LOAD", url.to(500));
		return $.ajax({
			url:url,
			method:"GET",
			cache:false,
			dataType:"json",
			headers:headers
		})
		.then(function(res){

			// record md5-s
			Object.keys(res._attachments||{})
			.forEach(function(i){
				if (items[i]) {
					items[i].state.remote = items[i].state.crc +"-" + res._attachments[i].digest;
				}
			});

			pi.resolve();
		})
		.fail(function(e){
			pi.reject(e);
		});
	}

