			html += elt.data;
			if (css.length) html+='</section>'
			html+= '\n\n';
			return;
		}

		// elt.format should be array
		// gen classes for sections
		var classes = '';
		if (Object.isString(elt.format)) elt.format = [elt.format];
		if (Object.isArray(elt.format)) {
			classes = elt.format.reduce(function(res,e){ 
				return res + ' in-item-' + (F[e]?F[e]:e);
			},'')
		}
		if (css.length) {
			classes +=' '+css.map(function(e){return "in-"+e.toLowerCase().replace(/[^a-z0-9]+/ig,'-');}).join(" ");
		} 

		// add embed
		if (elt.type == "embed") {
			html += '<figure class="' + classes + '">' +
				'<section>' + elt.data + '</section>' +
				(elt.showdesc+"" == "show" ? ('<figcaption class="in-item-desc">' + elt.desc + '</figcaption>') : '') +
				'</figure>' + '\n\n';
		}

		// add img

		if (elt.type == "img") {

			var obj, id, file, ref;

			// Create item of type:"attachment"
			if (elt.ref) {
				// image is attached
				ref = elt.ref.replace("inliner/"+docid+"/",'');
				id = cw.lib.hash8("inliner/" + docid +"/"+ref);

				if (data._attachments && data._attachments[elt.ref]) {
					file = data._attachments[elt.ref];
					obj = $.extend(true, {}, att0, {
						id: id,
						folder: "/inliner",
						name: docid+"/"+ref,
						mime: file.content_type,
						data: {
							content_type: file.content_type,
							data: file.data
						},
						state:{
							crc:cw.lib.sdbm(file.data)
						}
					});
					elt.ref = "inliner/"+ obj.name;
				} 
				else {
					obj = $.extend(true, {}, att0, {
						id: id,
						folder: "/inliner",
						name: docid+"/"+ref,
						state:{ crc:false }
					});
				}

			} else {
				// image is inlined
				id = cw.lib.hash8("inliner/" + docid +"/"+elt._stamp);
				obj = $.extend(true, {}, att0, {
					id: id,
					folder: "/inliner",
					name: docid+"/"+elt._stamp+".jpg",
					mime: "image/jpeg",
					data: {
						content_type: "image/jpeg",
						data: elt.data
					},
					state:{
						crc:cw.lib.sdbm(elt.data)
					}
				});

				elt.data = " ";
				elt.ref = "inliner/"+ obj.name;
			}

			if (obj) {
				// put new type:"attachment" item into main doc
				resrc[obj.name] = obj.id;
				if (
					 obj.state.crc && (
						!oitems[id] || 
						oitems[id].state.crc != obj.state.crc
					 )
				) oitems[id] = obj;
			}

			if (obj) html += '<figure class="' + classes + '">' +
				'<img src="' + obj.folder.slice(1) + '/' + obj.name + '"/>' +
				(elt.showdesc+"" == "show" ? ('<figcaption class="in-item-desc">' + elt.desc + '</figcaption>') : '') +
				'</figure>' + '\n\n';
		}

		// add widget-code
		if (elt.type == "code" && elt.tab && elt.tab.length) {
			html += '<div id="inliner-widget-code-'+elt._stamp+'" class="inliner-widget-code">';
			elt.tab.forEach(function(type){
				html += '<div id="x'+type+'" class="dib vat oh w750">' +
					'<textarea id="' + type + '" class="w750 fs80"></textarea>' +
					'</div>';
			});

			html += '</div>';

			html += '<script>' + 
				'$(function(){' +
				// TODO cut useless elt fields
				'var elt=' + cw.lib.js2txt(elt) + ';\n' +
				'initCode(elt);\n' +
				cw.lib.js2txt(initCode) +
				'})' +
				'</scr' + 'ipt>';
		}

		// add widget
		if (elt.type == "widget") {
			html += '<div id="inliner-widget-'+elt._stamp+'" class="inliner-widget"></div>';

			html += '<scr' + 'ipt>' + 
				'$(function(){' +
				'var elt=' + cw.lib.js2txt(elt) + ';\n' +
				'initWidget(elt);\n' +
				cw.lib.js2txt(initWidget) +
				'})' +
				'</scr' + 'ipt>';
		}
	});

	Object.keys(oitems).forEach(function(i){
		var it = oitems[i];
		if (it && it.folder=="/inliner" && it.name.startsWith(docid+"/")) {
			if (!resrc[it.name] || resrc[it.name] != it.id) oitems[i] = null;
		}
	});

	//item.data = JSON.stringify(data, null, '\t');
	item.data = meta + '\n' + html;

	return undefined;


