	// init widget
	function initWidget(elt) {
		var $elt = $('#inliner-widget-'+elt._stamp),
				man = {
					params:{strict:true},
					id:"cw.Inliner.Editor.Items.widget",
					Name:"App",
					Hoverpane:".cw-widget",
					data:{
						name: "",
						type: "widget",
						tab:["manifest"],
						js:"// Widget manifest\n({\n\tinit: function ($form, form) {\n\t\t// Init code\n\t},\n\tui: "
						+"{\n\t\t// UI bindings\n\t}\t,\n\tstyle: {\n\t\t// Local CSS rules\n\t}\n})",
						html:"",
						data:"({\n\t//Put default data here\n})",
						state:"",
						valid:false,
						err:[],
						options:[]
					},
					init: function ($o, form) {
						var that = this;
						$o.formgen(this.HTML);
						$o.then(function(){
							if (form.data.state) {
								$o.find("#btn-run").trigger("click");
							} else that.XCode($o, form.data);
						});
					},
					ui:{
						"#tab":{
							bind:function (d,v,$o){
								if (null!=v) {
									var $x = $o.my("find","#xcode");
									if ($x.is(".my-form")) $x.my("redraw");
									d.tab = v;
								}
								return d.tab;
							},
							init: function ($o) {
								$o.tags({tags:[{"HTML":"html"}, {"Manifest":"manifest"}, {"Defaults":"data"}]})
							}
						},
						"#btn-run":{
							bind: function (d,v,$o){
								if (v!=null) {
									if (d.valid) {
										d.state = "running";
										$o.my("find","#xcode,#tab").addClass("hide");
										var $a = $o.my("find","#xapp"),
												$v = $o.my("find","#xreflect");
										$a.removeClass("hide")
										.html(d.html)
										.my(eval(d.js),eval(d.data)||{})
										.then(function(){
											$a.trigger("change");
										}.debounce(50));

										if (d.options.indexOf("show data")!=-1) {
											$a.on("change.my", function(){
												$v.html(
													cw.lib.js2txt(
														$a.my("data"),"  ")
													.escapeHTML()
													.replace(/^\{/,'{ <span class="green">// Form data</span>')	
												);
											}.debounce(2));
											$a.addClass("xdata");
											$v.addClass("xdata");
										} else {
											$a.removeClass("xdata");
											$v.removeClass("xdata");
										}
									} else {
										cw.lib.note(d.err.join(".<br>")+".", "error", 5000);
									}
								}
							},
							css:{
								hide: function(d){return !!d.state},
								"red": function (d) {return !d.valid},
								"green": function (d) {return d.valid}
							},
							events:"click.my",
							watch:"#btn-code"
						},
						"#btn-code":{
							bind: function(d,v, $o){
								if (v!=null) {
									d.state = "";
									$o.my("find","#xapp").addClass("hide").my("remove");
									$o.my("find","#xcode, #tab").removeClass("hide");
									$o.my("find","#xreflect").removeClass("xdata");
									var $x = $o.my("find","#xcode");
									if (!$x.is(".my-form")) {
										this.XCode($o.my().root, d);
									}
								}
							},
							css:{
								hide: function(d){return !d.state}
							},
							events:"click.my",
							watch:"#btn-run"
						},
						"#warn":{
							bind:function (d) {
								var t=d.js, o, err=[], data;
								try { o = eval(t); } catch (e) {}
								try { data = eval(d.data); } catch (e) {}
								d.valid=false;
								if (!Object.isObject(o)) err.push("Invalid manifest");
								else {
									if ( !Object.isObject(o.ui)) err.push("No ui section");
									else if ( !Object.size(o.ui)) err.push("No bindings in ui section");

									if  ( !Object.isFunction(o.init) && !Object.isArray(o.init) && !(d.html+"").length)
										err.push("No HTML defined in init function or HTML tab");

									if (!Object.isObject(data))  err.push("Defaults must be an object");

									if (o.data!==undefined && !Object.isObject(o.data)) err.push("Section ‘data’ must be an object");
									if (o.params!==undefined && !Object.isObject(o.params)) err.push("Section ‘params’ must be an object");
									if (o.style!==undefined && !Object.isObject(o.style)) err.push("Section ‘style’ must be an object");
								}
								if (!err.length) d.valid = true;
								d.err=err;
