(function (data, item, oitems){

  var that = this,
      widgets = that.Types.rtf.extend.Inliner.Ed.Widgets;

	// Converts inliner docs to internal representation,
	// 
	item.doc = $.extend(!0, {}, Object.reject(data,['_attachments','previews']), {_id:item.id, previews:{}});
	item.data = "";

	// Reformat references
	// and move attachments from imported doc
	// to local

	var resrc = {}, 
			att0 = {
				id:"", folder:"", name:"", mime:"", parser:"", parserActive:false,
				ext:"", cache:"864000", data:"", snippet:false, size:"?",
				type:"attachment",
				desc:"", 
				state:{
					savedSdbm:"", sdbm:"Z",
					depends:{}, impacts:{}
				}
			},
			docid = item.doc._id;

	item.doc.content.forEach(function(elt){

    _grabFiles(elt)

	});

	Object.keys(oitems).forEach(function(i){
		var it = oitems[i];
		if (it && it.folder=="/inliner" && it.name.startsWith(docid+"/")) {
			if (!resrc[it.name] || resrc[it.name] != it.id) oitems[i] = null;
		}
	});

	return undefined;

  // - - - - - - - - - - -

  function _grabFiles(elt) {

    // Grabs attaches from doc and replace pointers

    // Widget ====================

    if (elt.type == 'widget') {
      var w;
      if (
        elt.source && elt.options.indexOf('autoupdate')>-1 
        && (w = widgets[elt.source])
        && w.To && w.To.files && w.To.data
      ) {
        var wdata = JSON.parse(elt.data.replace(/^\(|\)$/g,'')),
            files = w.To.files(wdata);

        Object.keys(files).forEach(function(fname){
        	if (!data._attachments || !data._attachments[fname]) return;
          var obj = _copyFile(fname);
          files[fname] = (obj.folder+'/'+obj.name).substr(1);
        });

        elt.data = w.To.data(elt, {Urls:files});
      }
    }

    // Image ====================

    if (elt.type == "img") { 

      var obj, id, file;

      // Create item of type:"attachment"
      if (elt.ref && data._attachments && data._attachments[elt.ref]) {
        // image is attached
        obj = _copyFile(elt.ref);
        elt.ref = (obj.folder+'/'+obj.name).substr(1);
      } 
      else {
        // image is inlined
        id = cw.lib.hash8("inliner/" + docid +"/"+elt._stamp);
        obj = $.extend(true, {}, att0, {
          id: id,
          folder: "/inliner",
          name: docid+"/"+elt._stamp+".jpg",
          mime: "image/jpeg",
          data: {content_type: "image/jpeg", data: elt.data},
          state:{ crc:cw.lib.sdbm(elt.data)}
        });

        elt.data = " ";
        elt.ref = "inliner/"+ obj.name;

      }

      if (obj) {
        // put new type:"attachment" item into main doc
        resrc[obj.name] = obj.id;
        if (!oitems[id] || oitems[id].state.crc != obj.state.crc) oitems[id] = obj;
      }

    }

  }

  // - - - - - - - - - - -

  function _copyFile (fname) {

    // copies attach from inliner to doc,
    // returns new fname

  	var id = cw.lib.hash8("inliner/" + docid +"/"+fname),
        file = data._attachments[fname];

    var obj = $.extend(true, {}, att0, {
      id: id,
      folder: "/inliner",
      name: docid+"/"+fname,
      mime: file.content_type,
      data: {
        content_type: file.content_type,
        data: file.data
      },
      state:{
        crc:cw.lib.sdbm(file.data)
      }
    });

    // put new type:"attachment" item into main doc
    resrc[obj.name] = obj.id;
    if (!oitems[id] || oitems[id].state.crc != obj.state.crc) oitems[id] = obj;

    return obj;
  }

})

